import React, { Component } from 'react';
import { TabContent, TabPane, Button, ButtonGroup } from 'reactstrap';
import StdCard from '../StdCard';
import CreateProjectFrom from '../../Forms/ProjectForms/CreateProjectForm/CreateProjectForm';
import EditProjectForm from '../../Forms/ProjectForms/EditProjectForm/EditProjectForm';
import DeleteProjectForm from '../../Forms/ProjectForms/DeleteProjectForm/DeleteProjectForm';

export default class ProjectCard extends Component {
   state = {
      theme: this.props.theme,
      sms: [],
      ops: [],
      activeTab: '3'
   };

   toggle(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   setContextInfo() {
      const { activeTab } = this.state;
      if (activeTab === '1') return 'Remove Project';
      if (activeTab === '2') return 'Create New Project';
      if (activeTab === '3') return 'Edit Project';
   }

   render() {
      const { theme } = this.state;

      return (
         <StdCard
            theme={theme}
            icon='fas fa-project-diagram'
            headerText='Projects Manager'
            size='xl'
         >
            <div>
               <h5>
                  {this.setContextInfo()}
                  <ButtonGroup className='float-right'>
                     <Button
                        size='sm'
                        color={
                           this.state.activeTab === '1'
                              ? 'primary'
                              : 'secondary'
                        }
                        onClick={() => {
                           this.toggle('1');
                        }}
                     >
                        <span className='fas fa-minus' />
                     </Button>
                     <Button
                        size='sm'
                        color={
                           this.state.activeTab === '2'
                              ? 'primary'
                              : 'secondary'
                        }
                        onClick={() => {
                           this.toggle('2');
                        }}
                     >
                        <span className='fas fa-plus' />
                     </Button>
                     <Button
                        size='sm'
                        color={
                           this.state.activeTab === '3'
                              ? 'primary'
                              : 'secondary'
                        }
                        onClick={() => {
                           this.toggle('3');
                        }}
                     >
                        <span className='fas fa-edit' />
                     </Button>
                  </ButtonGroup>
               </h5>
               <hr />
               <TabContent activeTab={this.state.activeTab}>
                  <TabPane tabId='1'>
                     <DeleteProjectForm theme={theme} />
                  </TabPane>
                  <TabPane tabId='2'>
                     <CreateProjectFrom theme={theme} />
                  </TabPane>
                  <TabPane tabId='3'>
                     <EditProjectForm theme={theme} />
                  </TabPane>
               </TabContent>
            </div>
         </StdCard>
      );
   }
}
