import React, { Component } from 'react'
import { Line, Bar, Pie, Doughnut, HorizontalBar, Radar, defaults } from 'react-chartjs-2'

//default chart options
defaults.global.defaultFontColor = 'lightgray';

export default class Chart extends Component {

   state = {
      _maintainAspectRatio : this.props._maintainAspectRatio
   }

   getChartOption(_displyGridLinesX = true,
                  _displyGridLinesY = true,
                  _isDoughnut = false
                  ){
      let _doughnutHole = 0;
      if( _isDoughnut ) _doughnutHole = 75;
      let chartOption= {
         fontColor: 'white',
         maintainAspectRatio: this.state._maintainAspectRatio,
         responsive: true,
         cutoutPercentage: _doughnutHole,
         scales: {
            ticks:{
               display: _isDoughnut
            },
            xAxes: [{
               gridLines: {
                  display: _displyGridLinesX,
                  drawBorder: false,
                  color: 'rgb(0,0,0,0.1)'
               }
            }],
            yAxes: [{
               gridLines: {
                  display: _displyGridLinesY,
                  drawBorder: false,
                  color: 'rgb(0,0,0,0.1)'
               }
            }]
         }
      }
      return chartOption;
   }

   chartOptionStd = {
      fontColor: 'white',
      maintainAspectRatio: true,
      responsive: true,
      scales: {
         xAxes: [{
            gridLines: {
               display: true,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }],
         yAxes: [{
            gridLines: {
               display: true,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }]
      }
   }

   chartOptionPie = {
      maintainAspectRatio: false,
      responsive: true,
      scales: {
         xAxes: [{
            ticks:{
               display: false
            },
            gridLines: {
               display: false,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }],
         yAxes: [{
            ticks:{
               display: false
            },
            gridLines: {
               display: false,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }]
      }
   }

   chartOptionDoughnut = {
      maintainAspectRatio: false,
      responsive: true,
      cutoutPercentage: 75,
      scales: {
         xAxes: [{
            ticks:{
               display: false
            },
            gridLines: {
               display: false,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }],
         yAxes: [{
            ticks:{
               display: false
            },
            gridLines: {
               display: false,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }]
      }
   }

   chartOptionRadar = {
      maintainAspectRatio: false,
      responsive: true,
      scales: {
         xAxes: [{
            gridLines: {
               display: false,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }],
         yAxes: [{
            gridLines: {
               display: false,
               drawBorder: false,
               color: 'rgb(0,0,0,0.1)'
            }
         }]
      }
   }

   getChart(chartType, data, height, width){
      if (chartType === 'Bar' | chartType === 'bar'){
         return <Bar options={this.getChartOption()} data={data} height={height} width={width}/>
      } 
      if (chartType === 'HorizontalBar' | chartType === 'horizontalBar' | chartType === 'hBar'){
         return <HorizontalBar options={this.getChartOption()} data={data} height={height} width={width}/>
      } 
      if (chartType === 'Line' | chartType === 'line'){
         return <Line options={this.getChartOption()} data={data} height={height} width={width}/>
      }
      if (chartType === 'Pie' | chartType === 'pie'){
         return <Pie options={this.getChartOption(false,false)} data={data} height={height} width={width}/>
      }
      if (chartType === 'Radar' | chartType === 'radar'){
         return <Radar options={this.getChartOption()} data={data} height={height} width={width}/>
      }
      if (chartType === 'Doughnut' | chartType === 'doughnut'){
         return <Doughnut options={this.getChartOption(false,false,true)} data={data} height={height} width={width}/>
      }
   }

   render() {

      const { chartType, data, height, width } = this.props;

      return (
         <div>
            {this.getChart(chartType,
                           data,
                           height,
                           width
            )}
         </div>
      )
   }
}
