import React, { Component } from 'react'
import '@fortawesome/fontawesome-free/css/all.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import './QuickView.css'
import ScoreCard from '../Card/ScoreCard';

export default class Quick extends Component {

   state = {
      headcount: {
         headcount: 0,
         headcountProc: 0,
         headcountTrend: 0,
         eHeadcount: 0,
         eHeadcountProc: 0,
         eHeadcountTrend: 0
      },
      productivity: {
         productivity: 0,
         productivityProc: 0,
         productivityTrend: 0,
         eProductivity: 0,
         eProductivityProc: 0,
         eProductivityTrend: 0
      },
      staff: {
         availability: 0,
         availabilityProc: 0,
         availabilityTrend: 0,
         shrinkage: 0,
         shrinkageProc: 0,
         shrinkageTrend: 0
      }
   }

   componentDidMount() {
      this.getAPIdata();
   }

   getAPIdata() {
      fetch('/api/prodsummary')
         .then(res => res.json())
         .then(({ 
            headcount: {
               headcount,
               headcountProc,
               headcountTrend,
               eHeadcount,
               eHeadcountProc,
               eHeadcountTrend
            },
            productivity: {
               productivity,
               productivityProc,
               productivityTrend,
               eProductivity,
               eProductivityProc,
               eProductivityTrend
            },
            staff: {
               availability,
               availabilityProc,
               availabilityTrend,
               shrinkage,
               shrinkageProc,
               shrinkageTrend
            }
         }) => this.setState({ 
            headcount: {
               headcount,
               headcountProc,
               headcountTrend,
               eHeadcount,
               eHeadcountProc,
               eHeadcountTrend
            },
            productivity: {
               productivity,
               productivityProc,
               productivityTrend,
               eProductivity,
               eProductivityProc,
               eProductivityTrend
            },
            staff: {
               availability,
               availabilityProc,
               availabilityTrend,
               shrinkage,
               shrinkageProc,
               shrinkageTrend
            }
         }))
   }

   render() {
      console.log(this.state);
      return (
         <div className="row">
            <ScoreCard icon="fa fa-users" title="Headcount" procValue={this.state.headcount.headcountProc} numberValue={this.state.headcount.headcount} trend={this.state.headcount.headcountTrend}></ScoreCard>
            <ScoreCard icon="fa fa-user" title="E.Headcount" procValue={this.state.headcount.eHeadcountProc} numberValue={this.state.headcount.eHeadcount} trend={this.state.headcount.eHeadcountTrend}></ScoreCard>
            <ScoreCard icon="fa fa-chart-line" title="Productivity" procValue={this.state.productivity.productivityProc} numberValue={this.state.productivity.productivity} trend={this.state.productivity.productivityTrend}></ScoreCard>
            <ScoreCard icon="fa fa-chart-line" title="E. Productivity" procValue={this.state.productivity.eProductivityProc} numberValue={this.state.productivity.eProductivity} trend={this.state.productivity.productivityTrend}></ScoreCard>
            <ScoreCard icon="fa fa-clock" title="Availability" procValue={this.state.staff.availabilityProc} numberValue={this.state.staff.availability} trend={this.state.staff.availabilityTrend}></ScoreCard>
            <ScoreCard icon="fa fa-stopwatch" title="Shrinkage" procValue={this.state.staff.shrinkageProc} numberValue={this.state.staff.shrinkage} trend={this.state.staff.shrinkageTrend}></ScoreCard>
         </div>
      )
   }
}
