import React, { Component } from 'react'
import { Input } from 'reactstrap'
import './Select.css'

export default class Select extends Component {

   state = {
      theme: this.props.theme,
      options: this.props.options
   }

   setTheme(theme){
      if(theme === 'dark'){
         return 'my-select-dark'
      }
      if(theme === 'light'){
         return ''
      }
   }

   renderOptions() {
      return (
         this.state.options.map((option, key) => {
            return <option key={key} value={option.value}>{option.name}</option>;
         }
         )
      )
   }

   render() {
      return (
         <Input className={this.setTheme(this.state.theme)} type="select" size="sm" name="select" id="exampleSelect">
            {this.renderOptions()}
         </Input>
      )
   }
}
