import React, { Component } from 'react';
import { HTMLSelect } from '@blueprintjs/core';
import Client from '../../../Modules/Client/Client';

export default class LanguageOptions extends Component {
   state = {
      language: '',
      languageArr: []
   };

   componentDidMount() {
      this.getOptions();
   }

   async getOptions() {
      try {
         var result = await Client.Services.LanguageService.getLanguages();
         this.setState({
            languageArr: result.data
         });
      } catch (err) {
         this.setState({
            languages: new Array('N/A')
         });
      }
   }

   renderOptions() {
      return this.state.languageArr.map((option, key) => {
         return (
            <option key={key} value={option._id}>
               {option.language}
            </option>
         );
      });
   }

   render() {
      return (
         <HTMLSelect
            id={this.props.id}
            ref={this.props.ref}
            onChange={this.props.onChange}
            disabled={this.props.disabled}
            fill={this.props.fill}
         >
            <option selected hidden disabled>
               {this.props.placeholder}
            </option>
            {this.renderOptions()}
         </HTMLSelect>
      );
   }
}
