import React, { Component } from 'react';
import { Table, Button, ButtonGroup } from 'reactstrap';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free';

export default class ProjectTable extends Component {
   constructor(props) {
      super(props);
      this.getProjects = this.getProjects.bind(this);
      this.renderTableHeaders = this.renderTableHeaders.bind(this);
      this.renderTableData = this.renderTableData.bind(this);
      this.handleClick = this.handleClick.bind(this);
      this.state = {
         theme: this.props.theme,
         data: this.props.data,
         filterName: this.props.name,
         projects: []
      };
   }

   componentDidMount() {
      this.getProjects();
   }

   getProjects() {
      const URL = 'http://localhost:3001/api/projects';

      axios
         .get(URL)
         .then(res => {
            this.setState({
               projects: res.data
            });
         })
         .catch(err => {
            alert('Error loading Projects table');
            console.log(JSON.stringify(err));
         });
   }

   setTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'table-dark';
      }
      if (theme === 'light') {
         return '';
      }
      console.log(theme);
   }

   renderTableHeaders() {
      return (
         <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Delivery Type</th>
            <th>SM</th>
            <th>OPS</th>
            <th className='text-center'>CIM</th>
            <th className='text-center'>RAS</th>
            <th className='text-center'>Action</th>
         </tr>
      );
   }

   addActionButton(project) {
      const { edit, remove, retire } = this.props;
      if (edit)
         return (
            <Button
               size='sm'
               onClick={e => this.handleClick(e, 'edit', project)}
            >
               <span className='fas fa-edit' />
            </Button>
         );
      if (remove && retire)
         return (
            <ButtonGroup>
               <Button
                  size='sm'
                  onClick={e => this.handleClick(e, 'remove', project)}
               >
                  <span className='fas fa-trash-alt' />
               </Button>
               <Button
                  size='sm'
                  onClick={e => this.handleClick(e, 'retire', project)}
               >
                  <span className='fas fa-hourglass-end' />
               </Button>
            </ButtonGroup>
         );
      if (remove)
         return (
            <Button
               size='sm'
               onClick={e => this.handleClick(e, 'remove', project)}
            >
               <span className='fas fa-trash-alt' />
            </Button>
         );
   }

   handleClick(e, action, project) {
      e.preventDefault();
      console.table(action + ' ' + project._id + ' ' + project.name);
      if (action === 'edit') this.editProject(project);
      if (action === 'remove') this.removeProject(project);
      if (action === 'retire') this.retireProject(project);
   }

   editProject(project) {
      /**
       *    TODO:
       *       -- modal project editor!
       */
   }

   removeProject(project) {
      const URL = 'http://localhost:3001/api/projects/' + project._id;

      axios
         .delete(URL)
         .then(res => {
            alert('Project ' + project.name + ' removed!');
            this.getProjects();
         })
         .catch(err => {
            alert('Something went wrong :(');
         });
   }

   retireProject(project) {
      /**
       *    TODO: retire project backed!
       */
      const URL = 'http://localhost:3001/api/projects/retire/' + project._id;

      axios
         .put(URL)
         .then(res => {
            alert('Project ' + project.name + ' is retired');
            this.getProjects();
         })
         .catch(err => {
            alert('Something went wrong :(');
         });
   }

   renderTableData() {
      return this.state.projects.map((project, index) => {
         return (
            <tr key={project._id}>
               <td>{project.name}</td>
               <td>{project.status.status}</td>
               <td>{project.deliveryType.deliveryType}</td>
               <td>{project.sm.name}</td>
               <td>{project.ops.name}</td>
               <td className='text-center'>{project.cim}</td>
               <td className='text-center'>{project.ras}</td>
               <td className='text-center'>{this.addActionButton(project)}</td>
            </tr>
         );
      });
   }

   render() {
      return (
         <div>
            <p>
               All Available Projects
               <Button
                  color='primary'
                  size='sm'
                  className='float-right'
                  onClick={this.getProjects}
               >
                  <span className='fas fa-sync-alt' />
               </Button>
            </p>
            <hr />
            <Table
               responsive
               borderless
               striped
               hover
               size='sm'
               className={this.setTheme()}
            >
               <thead>{this.renderTableHeaders()}</thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
         </div>
      );
   }
}
