import React, { Component } from 'react';
import StdCard from '../../../components/Card/StdCard';
import CreateuserForm from './CreateUserForm';
import Client from '../../../Modules/Client/Client';
import { Alert } from '@blueprintjs/core';

export default class CreateUserCard extends Component {
   state = {
      email: '',
      username: '',
      role: 'USER',
      password: '',
      openAlert: false,
      errorMessage: ''
   };
   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   closeAlert() {
      this.setState({ openAlert: false });
   }

   handleSubmit = async e => {
      e.preventDefault();

      const { email, username, role, password } = this.state;

      let usr = Client.Services.UsersService.createUserDTO(
         username,
         email,
         password,
         role,
         '5dfffe289fe19f21c85f3287', //<- temporaryfix
         '5dfffe289fe19f21c85f3287' //<- temporaryfix
      );
      try {
         let result = await Client.Services.UsersService.saveNewUser(usr);
         if (result.status === 200) {
            Client.Notifications.showSuccessNotification(
               `User ${username} successfully saved`
            );
         }
      } catch (err) {
         let msg;
         if (err.response.status === 409) {
            Client.Notifications.showAlertNotification(
               `Error: ${err.response.data.message}`
            );
            msg = `Error: ${err.response.data.message}`;
         }
         if (err.response.status !== 409) {
            Client.Notifications.showAlertNotification(
               `Error while saving ${username} user`
            );
            msg = `Error while saving ${username} user`;
         }
         this.setState({ openAlert: true, errorMessage: msg });
      }
   };
   render() {
      const { email, username, role, password, openAlert } = this.state;
      return (
         <div id='ceateUserContainer'>
            <Alert isOpen={openAlert}>Something went wrong</Alert>
            <StdCard headerText='Create new user' icon='add' size='m'>
               <CreateuserForm
                  email={email}
                  username={username}
                  password={password}
                  role={role}
                  onChange={this.onChange}
                  onClick={this.handleSubmit}
               />
            </StdCard>
         </div>
      );
   }
}
