import React, { Component } from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import '../Quic.css'

export default class ScoreCard extends Component {

   state = {
      name: 'N/A',
      proc: 0,
      number: 0,
      txtColor: 'text-gray'
   }

   getTrendIcon = (value) =>{
      if(value < 0) return " fas fa-arrow-alt-circle-down";
      else if (value > 0) return "fas fa-arrow-alt-circle-up";
      else return "fas fa-minus-circle"
   }

   getBgColor = (trend) => {

      this.state.txtColor = "text-white";

      if(trend < 0) return "bg-o-danger";

      else if (trend > 0) return "bg-o-success ";

      else {
         this.state.txtColor = "text-gray";
         return 'bg-c-white';
      }
   }

   render() {

      const { title, procValue, numberValue, trend} = this.props;

      return (
         <div className="col-sm-2 col-xs-6 mt-4" >
            <div className={`card ${this.getBgColor(trend)} ${this.state.txtColor}`}>
               <div className="card-body">
                  <h6>{title}
                     <span className={`${this.getTrendIcon(trend)} float-right`}>
                     </span>
                     <b class="float-right text-sm">{procValue}%</b>
                  </h6>
                  <h1>
                     <span className="fa fa-users"></span>
                     <span className="float-right text-big">{numberValue}</span>
                  </h1>
               </div>
            </div>
         </div>
      )
   }
}
