import React, { Component } from 'react';
import {
   Modal,
   Button,
   ButtonGroup,
   Card,
   CardBody,
   CardHeader
} from 'reactstrap';
import StdCard from '../StdCard';
import CreataeEmployeeForm from './CreateEmployeeForm/CreateEmployeeForm';
import EmployeeTable from '../../Table/EmployeeTable/EmployeeTable';

export default class EmployeeCard extends Component {
   constructor(props) {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = {
         theme: this.props.theme,
         modal: false
      };
   }

   toggle() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   setContextInfo() {
      return 'Manage your Employees';
   }

   setTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'table-dark';
      }
      if (theme === 'light') {
         return '';
      }
      console.log(theme);
   }

   render() {
      const { theme, modal, name } = this.state;

      return (
         <StdCard
            theme={theme}
            icon='fas fa-users'
            headerText='Employee Manager'
            size='xl'
         >
            <div>
               <h5>
                  {this.setContextInfo()}
                  <ButtonGroup className='float-right'>
                     <Button size='sm' color='primary' onClick={this.toggle}>
                        <span className='fas fa-user-plus' />
                     </Button>
                  </ButtonGroup>
               </h5>
               <hr />
               <EmployeeTable theme={theme} />
            </div>
            <Modal
               isOpen={modal}
               toggle={this.toggle}
               className='modal-full modal-my'
            >
               <Card className={this.setTheme(theme)}>
                  <CardHeader>
                     <span class='fas fa-chart-line header-icon' />
                     {'Edit Employee: ' + name}
                     <span
                        className='far fa-window-restore float-right header-icon text-secondary'
                        onClick={this.toggle}
                     />
                  </CardHeader>

                  <CardBody className='card-body'>
                     <CreataeEmployeeForm theme={theme} />
                  </CardBody>
               </Card>
            </Modal>
         </StdCard>
      );
   }
}
