import axios from 'axios'
import Auth from '../Auth/Auth'

export default class Client {

   /**
    * 
    * @param {String} username 
    * @param {String} password 
    * @return {}
    */
   static async login(username, password) {

      const data = {
         user: {
            email: username,
            password: password
         }
      };

      const url = '/api/users/login';

      return await axios
         .post(url, data)
         .then(result => {
            Auth.authenticateUser(result.data.user)
            return true
         })
         .catch(error => {
            console.log(error);
            return false
         });
   }

   /**
    * @return {boolean} - once completed
    */
   static async logout() {
      return await Auth.deauthenticateUser()
   }
}
