import React, { Component } from 'react';
import { Modal, CardBody } from 'reactstrap';
import { Card, Icon, Button, ButtonGroup } from '@blueprintjs/core';
import StdCard from './StdCard';
import CardHeader from './CardHeader';

export default class DataManagerCard extends Component {
   constructor(props) {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = {
         modal: false,
         tableData: this.props.data
      };
   }
   toggle() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }
   render() {
      const { modal } = this.state;

      return (
         <StdCard
            icon={this.props.icon}
            headerText={this.props.headerText}
            size='xl'
         >
            <h5>
               {this.props.cardCaption}
               <ButtonGroup className='float-right'>
                  <Button icon='add' minimal onClick={this.toggle}>
                     {this.props.addButtonText}
                  </Button>
               </ButtonGroup>
            </h5>
            <hr />

            <Modal
               id={this.props.id}
               isOpen={modal}
               toggle={this.toggle}
               className='modal-full modal-my'
            >
               <Card>
                  <CardHeader>
                     <Icon icon='user' className='header-icon' />
                     {this.props.modalHeaderText}
                     <div className='float-right'>
                        <Icon icon='minimize' onClick={this.toggle} />
                     </div>
                  </CardHeader>
                  <CardBody className='card-body'>
                     {this.props.addForm}
                  </CardBody>
               </Card>
            </Modal>
         </StdCard>
      );
   }
}

DataManagerCard.defaultProps = {
   icon: 'square',
   headerText: 'headerText',
   cardCaption: 'cardCaption',
   addButtonText: 'addButtonText',
   id: 'id',
   modalHeaderText: 'modalHeaderText',
   addForm: '<div></div>'
};
