import React, { Component } from 'react';
import {
   Row,
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   Label,
   Input
} from 'reactstrap';

import UIManager from '../../../Modules/UIManager/UIManager';
import StdCard from '../StdCard';
import ProjectTable from '../../Table/ProjectTable/ProjectTable';
import ProjectSelect from '../../Buttons/Select/ProjectSelect';

export default class ProjectCard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         projectName: '',
         projects: []
      };
   }

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   handleOnClick = e => {
      e.preventDefault();
      this.getProjects();
   };

   setSelectTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, projectName } = this.state;

      return (
         <StdCard
            theme={theme}
            icon='fas fa-project-diagram'
            headerText='Projects'
            size='m'
         >
            <div>
               <h5>
                  Project info
                  <Button size='sm' color='primary' className='float-right'>
                     <span className='fas fa-sync-alt' />
                  </Button>
               </h5>
               <hr />
            </div>
            <Form>
               <FormGroup row>
                  <Label for='projectName' md={3} className='text-right'>
                     Find Project
                  </Label>

                  <Col md={6}>
                     <ProjectSelect
                        name='projectName'
                        id='projectName'
                        theme={theme}
                     />
                  </Col>

                  <Col md={3}>
                     <Button color='primary' size='sm'>
                        Find
                     </Button>
                  </Col>
               </FormGroup>
            </Form>

            <ProjectTable theme={theme} name={projectName} />
         </StdCard>
      );
   }
}
