import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';

//import TopBar from '../Nav/TopBar/TopBar';
import QuickView from './QuickView/QuickView';
import ChartCard from '../../../components/Card/ChartCard/ChartCard';

const testData = require('../../../Data_OldStatic/old_data');

export default class MainDashboard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         showReportFilter: false,
         activeTab: '1'
      };
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   testd = testData;

   labels() {
      return this.testd.map(P => P.Project_Name);
   }

   productivityData() {
      return this.testd.map(P => P.TotalProductivity);
   }

   actualProductivityData() {
      return this.testd.map(P => P.ActualProductivity);
   }

   data = {
      labels: this.labels(),
      datasets: [
         {
            label: 'Data',
            backgroundColor: '#4099ff',
            borderColor: '#4099ff',
            data: this.productivityData()
         }
      ]
   };
   dataBar = {
      labels: this.labels(),
      datasets: [
         {
            label: 'Actual Productivity',
            backgroundColor: 'rgba(255,165,0,0.8)',
            data: this.actualProductivityData()
         },
         {
            label: 'Productivity',
            backgroundColor: 'rgba(0, 191, 255, 0.8)',
            data: this.productivityData()
         }
      ]
   };
   data_line = {
      labels: this.labels(),
      datasets: [
         {
            label: 'Actual Productivity',
            borderColor: 'rgba(0, 191, 255, 0.8)',
            backgroundColor: 'rgba(0,0,0,0)',
            data: this.actualProductivityData()
         },
         {
            label: 'Productivity',
            borderColor: 'rgba(255,165,0,0.8)',
            backgroundColor: 'rgba(0,0,0,0)',
            data: this.productivityData()
         }
      ]
   };
   dataRound = {
      labels: ['a', 'b'],
      datasets: [
         {
            label: 'Data',
            backgroundColor: ['#4099ff', '#FFF3201'],
            borderColor: 'transparent',
            data: [90, 10]
         }
      ]
   };

   switchShowReportFilter = () => {
      this.setState(state => ({ showReportFilter: !state.showReportFilter }));
   };

   render() {
      return (
         <div>
            <QuickView />
            <Row>
               <Col xl={6} lg={6} md={12} sm={12} xs={12} className='mt-3'>
                  <ChartCard
                     chartType='Bar'
                     data={this.dataBar}
                     headerText='Graf'
                     height={300}
                     width={700}
                  />
               </Col>
               <Col xl={6} lg={6} md={12} sm={12} xs={12} className='mt-3'>
                  <ChartCard
                     chartType='Line'
                     data={this.data_line}
                     headerText='Graf'
                     height={300}
                     width={700}
                  />
               </Col>
               <Col xl={6} lg={6} md={12} sm={12} xs={12} className='mt-3'>
                  <ChartCard
                     chartType='Line'
                     data={this.data}
                     headerText='Line_test'
                     height={300}
                     width={700}
                  />
               </Col>
               <ChartCard
                  chartType='Bar'
                  data={this.data}
                  headerText='Bar_test'
                  height={300}
                  width={700}
               />
               <ChartCard
                  chartType='Pie'
                  data={this.dataRound}
                  headerText='Pie Test'
                  size='s'
                  height={300}
                  width={700}
               />
               <ChartCard
                  chartType='Doughnut'
                  data={this.dataRound}
                  headerText='Doughnut test'
                  size='s'
                  height={300}
                  width={700}
               />
               <ChartCard
                  chartType='Radar'
                  data={this.dataBar}
                  headerText='Radar test'
                  height={300}
                  width={700}
                  size='s'
               />
               <ChartCard
                  chartType='hBar'
                  data={this.dataBar}
                  headerText='Horizontal Bar'
                  height={300}
                  width={700}
                  size='xl'
               />
            </Row>
         </div>
      );
   }
}
