import API from '../../API';
import Request from '../../Requests/Request';
import Employee from './DTO/Employee';

export default class EmployeesService {
   static async createEmployeeDTO(
      sap,
      name,
      role,
      supportType,
      project,
      rm,
      email,
      mainlanguage,
      mainlanguageLevel,
      secondlanguage,
      secondlanguageLevel,
      thirdlanguage,
      thirdlanguageLevel
   ) {
      return new Employee(
         sap,
         name,
         role,
         supportType,
         project,
         rm,
         email,
         mainlanguage,
         mainlanguageLevel,
         secondlanguage,
         secondlanguageLevel,
         thirdlanguage,
         thirdlanguageLevel
      ).getEmployeeDTO();
   }
   static async getEmployees() {
      return await Request.get(API.ROUTES.EMPLOYEES.GET_EMPLOYEES)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }
   static async getEmployeeById(id) {
      return await Request.get(API.ROUTES.EMPLOYEES.GET_EMPLOYEE_BY_ID + id)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async getEmployeeByName(name) {
      return await Request.get(API.ROUTES.EMPLOYEES.GET_EMPLOYEE_BY_NAME + name)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async createEmployee(employee) {
      return await Request.post(API.ROUTES.EMPLOYEES.CREATE_EMPLOYEE, employee)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async updateEmployee(employee) {
      return await Request.put(API.ROUTES.EMPLOYEES.UPDATE_EMPLOYEE, employee)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async deleteEmployeeById(id) {
      return await Request.delete(
         API.ROUTES.EMPLOYEES.DELETE_EMPLOYEE_BY_ID + id
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async getEmployeesRoles() {
      return await Request.get(API.ROUTES.EMPLOYEES_ROLES.GET_EMPLOYEES_ROLES)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async getEmployeesRoleById(id) {
      return await Request.get(
         API.ROUTES.EMPLOYEES_ROLES.GET_EMPLOYEES_ROLE_BY_ID + id
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async createEmployeeRole(role) {
      return await Request.post(
         API.ROUTES.EMPLOYEES_ROLES.CREATE_EMPLOYEES_ROLE,
         role
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }

   static async deleteEmployeeRoleById(id) {
      return await Request.delete(
         API.ROUTES.EMPLOYEES_ROLES.DELETE_EMPLOYEES_ROLE_BY_ID + id
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            throw err;
         });
   }
}
