import React, { Component } from 'react';
import { Redirect, Link } from 'react-router-dom';
import {
   Navbar,
   Button,
   Alignment,
   Menu,
   MenuItem,
   MenuDivider,
   Popover,
   Position,
   Icon
} from '@blueprintjs/core';
import UIManager from '../../Modules/UIManager/UIManager';
import Client from '../../Modules/Client/Client';
import './AppBar.css';

export default class AppBar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark',
         logout: false,
         goHome: false
      };
      this.switchTheme = this.switchTheme.bind(this);
      this.handleLogout = this.handleLogout.bind(this);
      this.handleGoHome = this.handleGoHome.bind(this);
   }

   switchTheme() {
      UIManager.swtichTheme();
      UIManager.setChartView();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      });
   }

   handleLogout = e => {
      e.preventDefault();
      Client.logout();
      this.setState({
         logout: true
      });
   };

   handleGoHome = e => {
      e.preventDefault();
      this.setState({
         goHome: true
      });
   };

   render() {
      if (this.state.logout) {
         return <Redirect to='/login' />;
      }

      if (this.state.goHome) {
         return <Redirect to='/home' />;
      }

      var exampleMenu = (
         <Menu>
            <MenuItem icon='user' text='Profile' />
            <MenuItem
               icon='cog'
               text='Settings...'
               href='/Settings/'
            ></MenuItem>
            <MenuItem
               icon={this.state.themeIcon}
               text={this.state.themeToSwitch + ' mode'}
               onClick={this.switchTheme}
            />
            <MenuDivider />
            <MenuItem
               icon='log-out'
               text='Logout...'
               onClick={this.handleLogout}
            ></MenuItem>
         </Menu>
      );
      var mainMenu = (
         <Menu>
            <MenuItem icon='application' text='About this app...' />
            <MenuItem
               icon='applications'
               text='Apps'
               popoverProps={{ openOnTargetFocus: false }}
               shouldDismissPopover={true}
            >
               <MenuItem icon='dashboard' text='Dashboard'></MenuItem>
               <MenuItem icon='projects' text='Project'></MenuItem>
               <MenuItem icon='manually-entered-data' text='Reports'></MenuItem>
               <MenuItem icon='control' text='Admin'></MenuItem>
            </MenuItem>
         </Menu>
      );

      return (
         <Navbar fixedToTop className='no-border border-bottom-0 pl-0'>
            <Navbar.Group align={Alignment.LEFT} className='adjust-nav-group '>
               <Popover
                  content={mainMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button minimal icon='circle' intent='primary'></Button>
               </Popover>
               <Navbar.Divider />
               <Button
                  minimal
                  icon='home'
                  href='/Home/'
                  onClick={this.handleGoHome}
               />
            </Navbar.Group>
            <Navbar.Group align={Alignment.RIGHT}>
               <Popover
                  content={exampleMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button className='bp3-minimal' icon='property' text='' />
               </Popover>
            </Navbar.Group>
         </Navbar>
      );
   }
}
