import React, { Component } from 'react';
import { Input, FormText } from 'reactstrap';
import axios from 'axios';
import { HTMLSelect } from '@blueprintjs/core';

export default class LanguageOptions extends Component {
   state = {
      language: '',
      languageArr: []
   };

   componentDidMount() {
      this.getOptions();
   }

   getOptions() {
      const URL = '/api/languages';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               language: res.data[0]._id,
               languageArr: res.data
            });
         })
         .catch(err => {
            console.log(JSON.stringify(err));
         });
   }

   renderOptions() {
      return this.state.languageArr.map((option, key) => {
         return (
            <option key={key} value={option._id}>
               {option.language}
            </option>
         );
      });
   }

   render() {
      return (
         <div>
            <HTMLSelect
               id={this.props.id}
               ref={this.props.ref}
               onChange={this.props.onChange}
               disabled={this.props.disabled}
               fill={this.props.fill}
            >
               <option selected hidden disabled>
                  {this.props.placeholder}
               </option>
               {this.renderOptions()}
            </HTMLSelect>
         </div>
      );
   }
}
