import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "../../App.css";
import "./MainContainer.css";

export default class MainContainer extends Component {
   state = {
      theme: this.props.theme,
      displayBgImage: this.displayBgImage(this.props.displayBgImage),
      displyBgAnimation: this.displyBgAnimation(this.props.displayBgAnimation)
   };

   setBgByTheme(theme) {
      if (theme === "dark") {
         return "main-data-bg-dark";
      }
      if (theme === "light") {
         return "main-data-bg-light";
      } else {
         return "main-data-bg-dark";
      }
   }

   displayBgImage(disply) {
      if (disply) {
         return "bg-image";
      }
      return "";
   }

   displyBgAnimation(disply) {
      if (disply) {
         return "bg-animation";
      }
      return "";
   }

   render() {
      return (
         <div
            className={`${this.setBgByTheme(this.state.theme)} 
                        ${this.displayBgImage(this.props.displayBgImage)}
                        ${this.displyBgAnimation(this.props.displayBgAnimation)}
                        main-data`}
         >
            {this.props.children}
         </div>
      );
   }
}
