import React, { Component } from 'react';
import { Tab, Tabs, Colors } from '@blueprintjs/core';
import { TabContent, TabPane } from 'reactstrap';
import Sidebar from '../../components/Sidebar/Sidebar';
import SidebarItem from '../../components/Sidebar/SidebarItem';
import Settings from '../../components/Settings/Settings';
//import ProjectsManagerCard from './components/ProjectCard/ProjectsManagerCard';
//import SLACard from './components/SLACard/SLACard';
//import EmployeeCard from './components/EmployeeCard/EmployeeCard';

export default class Projects extends Component {
   state = {
      activeTabID: 1,
      selected: 'A'
   };
   handleTabChange = TabId => this.setState({ activeTabID: 1 });

   changeSelection(id) {
      this.setState({ selected: id });
   }

   render() {
      const { selected } = this.state;

      return (
         <div>
            <Sidebar>
               <SidebarItem
                  icon='projects'
                  selected={selected === 'A'}
                  onClick={() => this.changeSelection('A')}
               />
               <SidebarItem
                  icon='user'
                  selected={selected === 'B'}
                  onClick={() => this.changeSelection('B')}
               />
               <SidebarItem
                  icon='locate'
                  selected={selected === 'C'}
                  onClick={() => this.changeSelection('C')}
               />
            </Sidebar>
            <TabContent activeTab={selected}>
               <div style={{ color: Colors.BLUE3 }}>Project info</div>

               <TabPane tabId='A'>
                  <Tabs onchange={this.handleTabChange} large>
                     <Tab id='1' title='Project' panel={4} />
                     <Tab id='2' title='Staff' panel={3} />
                     <Tab id='3' title='RM' panel={2} />
                     <Tab id='4' title='SLAs' panel={1} />
                  </Tabs>
               </TabPane>
               <TabPane tabId='B'>Employees in my Project</TabPane>
               <TabPane tabId='C'>
                  SLAs in my project
                  <Settings></Settings>
               </TabPane>
            </TabContent>
         </div>
      );
   }
}
