import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Menu, MenuItem } from '@blueprintjs/core';
import Client from '../../../Modules/Client/Client';
import UIManager from '../../../Modules/UIManager/UIManager';

export default class RightMenu extends Component {
   state = {
      theme: this.props.theme,
      themeIcon: this.props.theme === 'dark' ? 'flash' : 'moon',
      themeToSwitch: this.props.theme === 'dark' ? 'Light' : 'Dark',
      logout: false,
      goHome: false
   };

   switchTheme() {
      UIManager.swtichTheme();
      UIManager.setChartView();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         themeToSwitch: UIManager.isDarkThemeEnabled() ? 'Light' : 'Dark'
      });
   }

   handleLogout = e => {
      e.preventDefault();
      Client.logout();
      this.setState({
         logout: true
      });
   };

   render() {
      return (
         <Menu>
            <Link to='/UserProfile'>
               <MenuItem icon='user' text='Profile' />
            </Link>
            <Link to='/Settings'>
               <MenuItem icon='cog' text='Settings...' />
            </Link>
            <MenuItem
               icon={this.state.themeIcon}
               text={this.state.themeToSwitch + ' mode'}
               onClick={() => this.switchTheme}
            />
            <Menu.MenuDivider />
            <MenuItem
               icon='log-out'
               text='Logout...'
               onClick={this.props.handleLogout}
            ></MenuItem>
         </Menu>
      );
   }
}
