import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "@fortawesome/fontawesome-free";

import { Table } from "reactstrap";

export default class DataTable extends Component {
   state = {
      theme: this.props.theme,
      data: this.props.data,
      _testDataSet: [
         {
            id: 1,
            usertype: "adm",
            name: "RC",
            age: 29,
            email: "rafal.cymbalista@hcl.com"
         },
         {
            id: 2,
            usertype: "adm",
            name: "RC",
            age: 29,
            email: "rafal.cymbalista@hcl.com"
         },
         {
            id: 3,
            usertype: "adm",
            name: "RC",
            age: 29,
            email: "rafal.cymbalista@hcl.com"
         },
         {
            id: 4,
            usertype: "adm",
            name: "RC",
            age: 29,
            email: "rafal.cymbalista@hcl.com"
         },
         {
            id: 5,
            usertype: "adm",
            name: "RC",
            age: 29,
            email: "rafal.cymbalista@hcl.com"
         },
         {
            id: 6,
            usertype: "adm",
            name: "RC",
            age: 29,
            email: "rafal.cymbalista@hcl.com"
         }
      ]
   };

   /*TODO:
      -- Function to strip the data into the table values
         -- data structure : Headers + values
   */

   setTheme() {
      const { theme } = this.props;
      if (theme === "dark") {
         return "table-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   renderTableHeaders() {
      let header = Object.keys(this.state._testDataSet[0]);
      return header.map((key, index) => {
         return <th key={index}>{key}</th>;
      });
   }
   renderTableData() {
      return this.state._testDataSet.map((user, index) => {
         const { id, usertype, name, age, email } = user;
         return (
            <tr key={id}>
               <td>{id}</td>
               <td>{usertype}</td>
               <td>{name}</td>
               <td>{age}</td>
               <td>{email}</td>
            </tr>
         );
      });
   }

   render() {
      return (
         <div>
            <Table
               responsive
               borderless
               striped
               hover
               size="sm"
               className={this.setTheme()}
            >
               <thead>
                  <tr>{this.renderTableHeaders()}</tr>
               </thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
         </div>
      );
   }
}
