import React, { Component } from 'react';
import {
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   CustomInput,
   Label,
   Input,
   ButtonGroup
} from 'reactstrap';
import axios from 'axios';

import RoleOptions from '../Options/RoleOptions';
import SupportTypeOptions from '../Options/SupportTypeOptions';

export default class CreateContactTargetForm extends Component {
   state = {
      theme: this.props.theme,
      role: '',
      supportType: 'Dedicated',
      en: false,
      target: 0,
      message: '',
      supportTypeArr: ['Dedicated', 'Shared']
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   onSwitch = e => {
      const { en } = this.state;
      this.setState({ en: !en });
   };

   onSubmit = e => {
      e.preventDefault();

      const { role, supportType, en, target } = this.state;

      const URL = '/api/employees/contactTargets/';

      const data = {
         target: {
            role: role,
            supportType: supportType,
            en: en,
            target: target
         }
      };

      axios
         .post(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Contact Target saved successfully'
            });
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };

   clearForm = () => {
      this.setState({
         role: '',
         supportType: 'Dedicated',
         en: 'false',
         target: 0,
         message: ''
      });
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   render() {
      const { role, supportType, target, message } = this.state;

      return (
         <div>
            <h5>Create New Contact Target</h5>
            <hr />
            {message !== '' && (
               <Alert
                  className={`bg-transparent border rounded-corners text-center ${this.alertStyle()}`}
               >
                  {message}
               </Alert>
            )}

            <Form className='mb-3'>
               <FormGroup row>
                  <Label className='text-right' for='role' md={3} size='sm'>
                     Role
                  </Label>
                  <Col md={8}>
                     <RoleOptions
                        name='role'
                        value={role}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='email' md={3} size='sm'>
                     Support Type
                  </Label>
                  <Col md={8}>
                     <SupportTypeOptions
                        name='supportType'
                        value={supportType}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='Project' md={3} size='sm'>
                     EN / Non EN
                  </Label>
                  <Col md={4}>
                     <CustomInput
                        type='switch'
                        id='en'
                        name='en'
                        onChange={this.onSwitch}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='Project' md={3} size='sm'>
                     Target
                  </Label>
                  <Col md={8}>
                     <Input
                        type='number'
                        id='target'
                        name='target'
                        value={target}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>

               <div className='text-right'>
                  <ButtonGroup>
                     <Button size='sm' onClick={this.clearForm}>
                        Clear
                     </Button>
                     <Button color='primary' size='sm' onClick={this.onSubmit}>
                        Add
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
