import React, { Component } from 'react';
import { Modal, CardBody, CardHeader } from 'reactstrap';
import { Card, Icon, Button, ButtonGroup } from '@blueprintjs/core';
import StdCard from '../../../../components/Card/StdCard';
import CreataeEmployeeForm from '../../../../components/Forms/EmployeeForms/CreateEmployeeForm/CreateEmployeeForm';
//import EmployeeTable from '../../../../components/Table/EmployeeTable/EmployeeTable';
import axios from 'axios';

export default class EmployeeCard extends Component {
   constructor(props) {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = {
         modal: false,
         ops: [],
         sms: []
      };
   }

   componentDidMount() {
      this.getSms();
      this.getOps();
   }

   getSms() {
      const URL = 'http://localhost:3001/api/employees/role/Service%20Manager';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               sms: res.data
            });
         })
         .catch(err => {
            console.error('Somethig went wrong with loading SM list');
         });
   }

   getOps() {
      const URL =
         'http://localhost:3001/api/employees/role/Operations%20Manager';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               ops: res.data
            });
         })
         .catch(err => {
            console.error('Somethig went wrong with loading OPS list');
         });
   }

   toggle() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }
   render() {
      const { modal } = this.state;

      return (
         <StdCard icon='user' headerText='Employee Manager' size='xl'>
            <h5>
               Manage your Employees
               <ButtonGroup className='float-right'>
                  <Button icon='add' minimal onClick={this.toggle}>
                     Add new Employee
                  </Button>
               </ButtonGroup>
            </h5>
            <hr />

            <Modal
               isOpen={modal}
               toggle={this.toggle}
               className='modal-full modal-my'
            >
               <Card>
                  <CardHeader>
                     <Icon icon='user' className='header-icon' />
                     Create new Employee
                     <div className='float-right'>
                        <Icon icon='minimize' onClick={this.toggle} />
                     </div>
                  </CardHeader>
                  <CardBody className='card-body'>
                     <CreataeEmployeeForm />
                  </CardBody>
               </Card>
            </Modal>
         </StdCard>
      );
   }
}
