import React, { Component } from 'react';
import { Row } from 'reactstrap';
import { Alert, H5, Portal } from '@blueprintjs/core';
import ProjectForm from '../../../components/Forms/ProjectForm';
import Client from '../../../Modules/Client/Client';
import DataManagerCard from '../../../components/Card/DataManagerCard';
import ProjectsTable from './ProjectsTable';

export default class ProjectsCard extends Component {
   state = {
      isLoading: 'false',
      email: '',
      Projectname: '',
      Projecttype: 'Project',
      password: '',
      openAlert: false,
      errorMessage: '',
      errorMsg: ''
   };

   componentDidMount() {
      this.getProjectsData();
   }

   async getProjectsData() {
      this.setLoading(true);
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.ProjectsService.getProjects();
         this.setState({
            Projects: result.data
         });
      } catch (err) {
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading Projects list');
      } finally {
         this.setLoading(false);
      }
   }
   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openAlert(msg) {
      this.setState({ openAlert: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ openAlert: false, errorMessage: '' });
   }

   clearForm() {
      this.setState({
         email: '',
         Projectname: '',
         Projecttype: 'standard',
         password: '',
         openAlert: false,
         errorMessage: ''
      });
   }

   handleSubmit = async e => {
      e.preventDefault();
      const { email, Projectname, Projecttype, password } = this.state;
      var errorMsg = 'Unknown Error';
      let usr = Client.Services.ProjectsService.createProjectDTO(
         Projectname,
         email,
         password,
         Projecttype
      );
      try {
         let result = await Client.Services.ProjectsService.saveNewProject(usr);
         if (result.status === 200) {
            Client.Notifications.success(
               `Project ${Projectname} successfully saved`
            );
            this.getProjectsData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${Projectname} Project`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };

   render() {
      const {
         isLoading,
         email,
         Projectname,
         Projecttype,
         password,
         Projects,
         errorMessage,
         openAlert
      } = this.state;
      return (
         <Row>
            <DataManagerCard
               icon='gantt-chart'
               cardSize='m'
               headerText='Projects'
               cardCaption='All app Projects'
               addButtonText='Add new Project'
               id='allProjects'
               modalHeaderText='Add new Project'
               onRefresh={() => this.getProjectsData()}
               table={
                  <ProjectsTable
                     ProjectsData={Projects}
                     onRefresh={() => this.getProjectsData()}
                     isLoading={isLoading}
                  ></ProjectsTable>
               }
               addForm={
                  <ProjectForm
                     email={email}
                     Projectname={Projectname}
                     password={password}
                     Projecttype={Projecttype}
                     onChange={this.onChange}
                     onClick={this.handleSubmit}
                     buttonCaption='Create'
                     onClear={() => this.clearForm()}
                  />
               }
            >
               <Alert
                  isOpen={openAlert}
                  confirmButtonText='Clear form'
                  onConfirm={() => this.clearForm()}
                  cancelButtonText='Cancel'
                  onCancel={() => this.closeAlert()}
                  intent='danger'
                  icon='issue'
                  ussePortal={
                     <Portal
                        container={document.getElementById('allProjects')}
                     />
                  }
               >
                  <H5>{errorMessage}</H5>
                  <strong>Do you want to clear the data?</strong>
               </Alert>
            </DataManagerCard>
         </Row>
      );
   }
}
