import React, { Component } from 'react';
import {
   Row,
   Form,
   FormGroup,
   Label,
   CustomInput,
   TabContent,
   TabPane
} from 'reactstrap';
import TopBar from '../Nav/TopBar/TopBar';
import LeftBar from '../Nav/LeftBar/LeftBar';
import NavItem from '../Nav/NavItem/NavItem';
import StdCard from '../Card/StdCard';
import MainContainer from '../MainContainer/MainContainer';
import UIManager from '../../Modules/UIManager/UIManager';

export default class Settings extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         showReportFilter: false,
         activeTab: '1'
      };
      this.switchTheme = this.switchTheme.bind(this);
   }

   switchTheme() {
      const { theme } = this.state;

      if (theme === 'dark') {
         UIManager.setTheme('light');
      }
      if (theme === 'light') {
         UIManager.setTheme('dark');
      }
      this.setState({
         theme: UIManager.getTheme()
      });
      document.body.className =
         UIManager.getTheme() === 'dark' ? 'bp3-dark' : 'bp3-body';
   }

   isDarkModeEnabled() {
      if (UIManager.getTheme() === 'dark') return true;
      return false;
   }

   setTheme(theme) {
      if (theme === 'dark') {
         return 'table-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }

   setInputTheme(theme) {
      if (theme === 'dark') {
         return 'table-input-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   render() {
      return (
         <MainContainer theme={this.state.theme}>
            <TopBar theme={this.state.theme} />
            <LeftBar theme={this.state.theme}>
               <NavItem
                  icon='fa fa-cog'
                  active={this.state.activeTab === '1'}
                  onClick={() => {
                     this.switchTab('1');
                  }}
               />
            </LeftBar>
            <TabContent activeTab={this.state.activeTab}>
               <TabPane tabId='1'>
                  <Row>
                     <StdCard
                        icon='fas fa-cogs'
                        headerText='Application settings'
                        theme={this.state.theme}
                     >
                        <h6 for='exampleCheckbox mb-o'>
                           <span className='fas fa-palette mr-2' />
                           User Interface
                        </h6>
                        <hr />
                        <Form>
                           <FormGroup>
                              <Label for='exampleCheckbox mb-o'>Theme</Label>
                              <CustomInput
                                 type='switch'
                                 id='UITheme'
                                 name='UITheme'
                                 label='Enable dark mode'
                                 onChange={this.switchTheme}
                                 checked={this.isDarkModeEnabled()}
                              />
                           </FormGroup>
                        </Form>
                     </StdCard>
                  </Row>
               </TabPane>
            </TabContent>
         </MainContainer>
      );
   }
}
