import React, { Component } from 'react';
import '../../App.css';

import 'bootstrap/dist/css/bootstrap.css';
import '@fortawesome/fontawesome-free/css/all.css';

import { Row, TabContent, TabPane } from 'reactstrap';
import TopBar from '../Nav/TopBar/TopBar';
import LeftBar from '../Nav/LeftBar/LeftBar';
import NavItem from '../Nav/NavItem/NavItem';
import QuickView from '../QuickView/QuickView';
import ReportControl from '../ReportControl/ReportControl';
import ChartCard from '../Card/ChartCard/ChartCard';
import StdCard from '../Card/StdCard';
import DataTable from '../Table/DataTable';
import InputTable from '../Table/InputTable';
import MainContainer from '../MainContainer/MainContainer';
import TeamTable from '../Table/TeamTable/TeamTable';

import ProjectsManagerCard from '../Card/ProjectCard/ProjectsManagerCard';

import UIManager from '../../Modules/UIManager/UIManager';

const testData = require('../../Data_OldStatic/old_data');

export default class MainDashboard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         showReportFilter: false,
         activeTab: '1'
      };
   }

   setBgByTheme(theme) {
      if (theme === 'dark') {
         return 'main-data-bg-dark';
      }
      if (theme === 'light') {
         return 'main-data-bg-light';
      } else {
         return 'main-data-bg-dark';
      }
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   testd = testData;

   labels() {
      return this.testd.map(P => P.Project_Name);
   }

   productivityData() {
      return this.testd.map(P => P.TotalProductivity);
   }

   actualProductivityData() {
      return this.testd.map(P => P.ActualProductivity);
   }

   data = {
      labels: this.labels(),
      datasets: [
         {
            label: 'Data',
            backgroundColor: '#4099ff',
            borderColor: '#4099ff',
            data: this.productivityData()
         }
      ]
   };
   dataBar = {
      labels: this.labels(),
      datasets: [
         {
            label: 'Actual Productivity',
            backgroundColor: 'rgba(255,165,0,0.8)',
            data: this.actualProductivityData()
         },
         {
            label: 'Productivity',
            backgroundColor: 'rgba(0, 191, 255, 0.8)',
            data: this.productivityData()
         }
      ]
   };
   data_line = {
      labels: this.labels(),
      datasets: [
         {
            label: 'Actual Productivity',
            borderColor: 'rgba(0, 191, 255, 0.8)',
            backgroundColor: 'rgba(0,0,0,0)',
            data: this.actualProductivityData()
         },
         {
            label: 'Productivity',
            borderColor: 'rgba(255,165,0,0.8)',
            backgroundColor: 'rgba(0,0,0,0)',
            data: this.productivityData()
         }
      ]
   };
   dataRound = {
      labels: ['a', 'b'],
      datasets: [
         {
            label: 'Data',
            backgroundColor: ['#4099ff', '#FFF3201'],
            borderColor: 'transparent',
            data: [90, 10]
         }
      ]
   };

   switchShowReportFilter = () => {
      this.setState(state => ({ showReportFilter: !state.showReportFilter }));
   };

   render() {
      const { theme, isLoading } = this.state;

      return (
         <MainContainer theme={theme}>
            <TopBar theme={theme}>
               <NavItem
                  icon='fas fa-filter'
                  onClick={this.switchShowReportFilter}
               />
            </TopBar>
            <LeftBar theme={theme}>
               <NavItem
                  icon='fa fa-signal'
                  active={this.state.activeTab === '1'}
                  onClick={() => {
                     this.switchTab('1');
                  }}
               />
               <NavItem
                  icon='fa fa-users'
                  active={this.state.activeTab === '2'}
                  onClick={() => {
                     this.switchTab('2');
                  }}
               />
               <NavItem
                  icon='fa fa-check-square '
                  active={this.state.activeTab === '3'}
                  onClick={() => {
                     this.switchTab('3');
                  }}
               />
            </LeftBar>
            <TabContent activeTab={this.state.activeTab} className='mb-5'>
               <TabPane tabId='1'>
                  <ReportControl
                     theme={theme}
                     show={this.state.showReportFilter}
                  />
                  <QuickView theme={theme} />
                  <Row>
                     <ChartCard
                        theme={theme}
                        chartType='Bar'
                        data={this.dataBar}
                        headerText='Graf'
                        height={300}
                        width={700}
                     />
                     <ChartCard
                        theme={theme}
                        chartType='Line'
                        data={this.data_line}
                        headerText='Graf'
                        height={300}
                        width={700}
                     />
                     <ChartCard
                        theme={theme}
                        chartType='Line'
                        data={this.data}
                        headerText='Line_test'
                        height={300}
                        width={700}
                     />
                     <ChartCard
                        theme={theme}
                        chartType='Bar'
                        data={this.data}
                        headerText='Bar_test'
                        height={300}
                        width={700}
                     />
                     <ChartCard
                        theme={theme}
                        chartType='Pie'
                        data={this.dataRound}
                        headerText='Pie Test'
                        size='s'
                        height={300}
                        width={700}
                     />
                     <ChartCard
                        theme={theme}
                        chartType='Doughnut'
                        data={this.dataRound}
                        headerText='Doughnut test'
                        size='s'
                        height={300}
                        width={700}
                     />
                     <ChartCard
                        theme={theme}
                        chartType='Radar'
                        data={this.dataBar}
                        headerText='Radar test'
                        height={300}
                        width={700}
                        size='s'
                     />
                     <ChartCard
                        theme={theme}
                        chartType='hBar'
                        data={this.dataBar}
                        headerText='Horizontal Bar'
                        height={300}
                        width={700}
                        size='xl'
                     />
                  </Row>
               </TabPane>
               <TabPane tabId='2'>
                  <ReportControl
                     theme={theme}
                     show={this.state.showReportFilter}
                  />
                  <Row>
                     <StdCard
                        theme={theme}
                        icon='fa fa-table'
                        headerText='table _test'
                     >
                        <InputTable theme={theme} />
                     </StdCard>
                     <StdCard
                        theme={theme}
                        icon='fa fa-table'
                        headerText='table _test'
                     >
                        <DataTable theme={theme} />
                     </StdCard>
                     <StdCard
                        theme={theme}
                        icon='fa fa-table'
                        headerText='table _test'
                        size='xl'
                     >
                        <TeamTable theme={theme} />
                     </StdCard>
                  </Row>
               </TabPane>
               <TabPane tabId='3'>
                  <ProjectsManagerCard theme={theme} />
               </TabPane>
            </TabContent>
         </MainContainer>
      );
   }
}
