import React, { Component } from 'react';
import { Form, FormGroup, Label } from 'reactstrap';
import ProjectTable from '../../../Table/ProjectTable/ProjectTable';

export default class DeleteProjectForm extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: this.props.theme,
         projectName: '',
         projects: []
      };
   }

   setSelectTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, projectName } = this.state;

      return (
         <div>
            <Form>
               <FormGroup row>
                  <Label for='projectName' md={12} className='text-center'>
                     Find Project to remove
                  </Label>
               </FormGroup>
            </Form>
            <ProjectTable theme={theme} name={projectName} remove retire />
         </div>
      );
   }
}
