import { Toaster } from '@blueprintjs/core';

const doToast = Toaster.create({
   className: 'mt-5',
   position: 'top-right',
   maxToasts: 10
});

export default class Notifications {
   static showNotification(message) {
      doToast.show({ message: message });
   }
   static showSuccessNotification(message) {
      doToast.show({
         message: message,
         intent: 'success',
         icon: 'tick'
      });
   }
   static showAlertNotification(message) {
      doToast.show({
         message: message,
         intent: 'warning',
         icon: 'warning-sign'
      });
   }
   static showFailuretNotification(message) {
      doToast.show({
         message: message,
         intent: 'danger',
         icon: 'error'
      });
   }
   static showCustomNotification(message, intent, icon, timeout) {
      doToast.show({
         message: message,
         intent: intent,
         icon: icon,
         timeout: timeout
      });
   }
}
