import React, { Component } from 'react';
import {
   Table,
   ButtonGroup,
   Modal,
   Card,
   CardHeader,
   CardBody
} from 'reactstrap';
import { Button, Spinner, NonIdealState } from '@blueprintjs/core';
import UpdateslaForm from '../../../../components/Forms/SLAsForms/UpdateSLAForm';

import axios from 'axios';
import Client from '../../../../Modules/Client/Client';

export default class SLAsTable extends Component {
   constructor(props) {
      super(props);
      this.getSLAs = this.getSLAs.bind(this);
      this.renderTableHeaders = this.renderTableHeaders.bind(this);
      this.renderTableData = this.renderTableData.bind(this);
      this.handleClick = this.handleClick.bind(this);
      this.closeModal = this.closeModal.bind(this);
      this.state = {
         modal: false,
         isLoading: true,
         id: '',
         projectName: '',
         slaType: '',
         SLA: '',
         description: '',
         logic: '',
         target: '',
         slaArr: []
      };
   }

   componentDidMount() {
      this.getSLAs();
   }

   getSLAs() {
      this.setState({ isLoading: true });
      try {
         var result = Client.Services.SLAsService.getSLAs();
         this.setState({ slaArr: result.data });
      } catch (err) {
         console.log(err);
      } finally {
         this.setState({ isLoading: false });
      }
   }

   addActionButton(sla) {
      return (
         <ButtonGroup>
            <Button
               size='sm'
               color='danger'
               onClick={e => this.handleClick(e, 'remove', sla)}
            >
               <span className='fas fa-trash-alt' />
            </Button>
            <Button size='sm' onClick={e => this.handleClick(e, 'edit', sla)}>
               <span className='fas fa-edit' />
            </Button>
         </ButtonGroup>
      );
   }

   handleClick(e, action, sla) {
      e.preventDefault();
      if (action === 'edit') this.editsla(sla);
      if (action === 'remove') this.removesla(sla);
   }

   editsla(sla) {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));

      this.setState({
         id: sla._id,
         projectName: sla.project.name,
         slaType: sla.type.type,
         SLA: sla.SLA,
         description: sla.description,
         logic: sla.logic,
         target: sla.target
      });
   }

   closeModal() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   removesla(sla) {
      const URL = '/api/sla/' + sla._id;
      axios
         .delete(URL)
         .then(res => {
            alert('sla ' + sla.SLA + ' removed!');
            document.getElementById('getslasData').click();
         })
         .catch(err => {
            alert('Something went wrong :(');
         });
   }

   renderTableHeaders() {
      return (
         <tr className='bg-black text-white'>
            <th>Project</th>
            <th>SLA Type</th>
            <th>SLA</th>
            <th>Description</th>
            <th>Target</th>
            <th>Logic</th>
            <th>Action</th>
         </tr>
      );
   }

   renderTableData() {
      return this.state.slaArr.map((sla, index) => {
         return (
            <tr key={sla._id}>
               <td>{sla.project.name}</td>
               <td>{sla.type.type}</td>
               <td>{sla.SLA}</td>
               <td>{sla.description}</td>
               <td>{sla.target}</td>
               <td>{sla.logic}</td>
               <td>{this.addActionButton(sla)}</td>
            </tr>
         );
      });
   }

   render() {
      const {
         slaArr,
         id,
         isLoading,
         modal,
         projectname,
         slaType,
         SLA,
         description,
         logic,
         target
      } = this.state;

      if (isLoading) {
         return <Spinner intent='primary' />;
      }

      if (!slaArr || slaArr.length === 0) {
         return (
            <NonIdealState icon='issue'>
               <h6 className='text-secondary'>No SLAs found...</h6>
               <Button
                  minimal
                  icon='refresh'
                  onClick={() => this.getSLAs()}
               ></Button>
            </NonIdealState>
         );
      }

      return (
         <div>
            <Table responsive borderless striped hover size='sm'>
               <thead>{this.renderTableHeaders()}</thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
            <Modal isOpen={modal} toggle className='modal-full modal-my'>
               <Card>
                  <CardHeader>
                     <span class='fas fa-chart-line header-icon' />
                     {'Edit sla: ' + SLA}
                     <span
                        className='far fa-window-restore float-right header-icon text-secondary'
                        onClick={this.closeModal}
                     />
                  </CardHeader>
                  <CardBody>
                     <UpdateslaForm
                        id={id}
                        projectname={projectname}
                        slaType={slaType}
                        SLA={SLA}
                        description={description}
                        target={target}
                        logic={logic}
                     />
                  </CardBody>
               </Card>
            </Modal>
         </div>
      );
   }
}

