import React, { Component } from 'react';
import { Card } from '@blueprintjs/core';
import '../QuickView/QuickView.css';
import './ScoreCard.css';

import { Col, CardBody, CardText, Badge } from 'reactstrap';

/*TODO:
   -- fix title / badge relation
      -- overlaping title over badge
      -- hight issue on mid horizontal size
      -- rounding corners! 10px
   FIXME:
   -- state mutation warrning!
*/
export default class ScoreCard extends Component {
   state = {
      name: 'N/A',
      icon: '',
      proc: 0,
      number: 0,
      bgColor: 'bg-c-white',
      txtColor: 'text-gray'
   };

   componentDidMount() {
      this.getBgColor(this.props.trend);
   }

   getTrend = value => {
      if (value < 0) return '-';
      else if (value > 0) return '+';
      else return ' ';
   };

   getBgColor = trend => {
      this.setState({
         txtColor: 'text-white'
      });

      let bgColor;
      if (trend < 0) bgColor = 'bg-danger';
      else if (trend > 0) bgColor = 'bg-success ';
      else {
         bgColor = '';
         this.setState({
            txtColor: 'text-black'
         });
         return '';
      }

      this.setState({
         txtColor: bgColor === '' ? 'text-back' : 'text-wite',
         bgColor: bgColor
      });
   };

   getIcon = iconName => {
      if (!iconName) return 'fas fa-bug';
      else return iconName;
   };

   render() {
      const { title, icon, procValue, numberValue } = this.props;

      return (
         <Col xl='2' lg='3' md='3' sm='6' xs='12' className='mt-4'>
            <Card className='mb-0 score-card p-3'>
               <div className='mt-2 mr-3 ml-3'>
                  {title}
                  <Badge
                     className={`${this.state.txtColor} ${this.state.bgColor} ml-auto float-right`}
                  >
                     {this.getTrend(this.props.trend) + procValue}%
                  </Badge>
               </div>
               <CardBody>
                  <CardText>
                     <h1>
                        <span
                           className={`${this.getIcon(
                              icon
                           )} text-secondary icon-big`}
                        />
                        <span className='float-right text-mid mr-0 score-numbers'>
                           {numberValue}
                        </span>
                     </h1>
                  </CardText>
               </CardBody>
            </Card>
         </Col>
      );
   }
}
