import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   Card,
   Icon
} from '@blueprintjs/core';

export default class UsersTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      userForEdit: {},
      selected: ''
   };

   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th className='text-center pl-3'>#</th>
               <th>User Name</th>
               <th>Email</th>
               <th className='text-center'>Role</th>
               <th className='text-center'>Active</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { usersData } = this.props;
      return usersData.map((user, key) => {
         return (
            <tr
               key={user._id}
               onClick={e => this.handleSelection(e, user)}
               className={`pl-2 ${
                  this.state.selected._id === user._id ? 'bg-selected' : ''
               }`}
            >
               <td className='text-center'>{key}</td>
               <td style={{ paddingLeft: '20px' }}>{user.username}</td>
               <td>{user.email}</td>
               <td className='text-center'>{user.usertype}</td>
               <td className='text-center'>
                  {user.active !== null && user.active ? (
                     <Icon icon='tick' intent='success' />
                  ) : (
                     <Icon icon='small-cross' intent='danger' />
                  )}
               </td>
            </tr>
         );
      });
   }

   handleSelection(e, user) {
      e.preventDefault();
      this.props.onSelection(user, false);
      this.setState({
         selected: user
      });
   }

   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load users table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => {
                     this.props.onRefresh();
                     this.setState({
                        errorMsg: ''
                     });
                  }}
               ></Button>
            </NonIdealState>
         );
      }
      return (
         <div className='overflow-x-scroll'>
            <HTMLTable fill='true' striped condensed interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }
   render() {
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }
      return <Card elevation='2'>{this.renderContent()}</Card>;
   }
}
UsersTable.defaultProps = {
   usersData: [],
   onRefresh: function() {
      return;
   }
};
