import API from '../../API';
import Request from '../../Requests/Request';
import Language from './DTO/Language';

export default class Languageservice {
   /**
    * @public
    * @param {language} language
    * @returns {Language} DTO
    */
   static createLanguageDTO(language) {
      return new Language(language).getLanguageDTO();
   }
   /**
    * @public
    */
   static async getLanguages() {
      return await Request.get(API.ROUTES.LANGUAGES.GET_LANGUAGES);
   }
   /**
    * @public
    * @param {String} language
    */
   static async saveNewLanguage(language) {
      let lang = this.createLanguageDTO(language);
      return await Request.post(API.ROUTES.LANGUAGES.CREATE_NEW_LANGUAGE, lang)
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }

   /**
    * @public
    * @param {String} id
    */
   static async getLanguageByID(id) {
      return await Request.get(API.ROUTES.LANGUAGES.GET_LANGUAGE_BY_ID + id)
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
   /**
    * @public
    * @param {String} name
    */
   static async getLanguageByName(name) {
      return await Request.get(API.ROUTES.LANGUAGES.GET_LANGUAGE_BY_NAME + name)
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
   /**
    *
    * @param {Language} language
    */
   static async updateLanguage(language) {
      return await Request.put(API.ROUTES.LANGUAGES.UPDATE_LANGUAGE)
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }

   static async deleteLanguageByID(id) {
      return await Request.delete(API.ROUTES.LANGUAGES.UPDATE_LANGUAGE + id)
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
}
