         'light': 'rgba(74, 144, 226, 1)',
         'main': 'rgba(0, 122, 255, 1)',
         'dark': '#303f9f',
         'contrastText': '#fff'
      },
      secondary: {
         'light': '#ff4081',
         'main': 'rgba(74, 74, 74, 1)',
         'dark': '#c51162',
         'contrastText': '#fff'
      },
      error: {
         'light': '#e57373',
         'main': '#f44336',
         'dark': '#d32f2f',
