import React, { Component } from 'react';
import { Form } from 'reactstrap';
import {
   Alert,
   Button,
   ButtonGroup,
   FormGroup,
   InputGroup,
   HTMLTable,
   HTMLSelect,
   H5
} from '@blueprintjs/core';
import axios from 'axios';
import EmployeeRoleOptions from '../../../components/Selects/EmployeeRoleOptions';
import SupportTypeOptions from '../../../components/Selects/SupportTypeOptions';
import LanguageOptions from '../../../components/Selects/LanguageOptions';
import ProjectOptions from '../../../components/Selects/ProjectOptions';
import RMOptions from '../../../components/Selects/RMOptions';

export default class CreateEmployeeForm extends Component {
   state = {
      theme: this.props.theme,
      sap: '',
      name: '',
      email: '',
      role: '',
      supportType: 'Dedicated',
      project: '',
      mainlanguage: '',
      mainlanguagelevel: '',
      secondlanguage: '',
      secondlanguagelevel: '',
      thirdlanguage: '',
      thirdlanguagelevel: '',
      rm: '',
      message: '',
      supportTypeArr: []
   };

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   onSubmit = e => {
      e.preventDefault();

      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      } = this.state;

      const URL = '/api/employees/';

      const data = {
         employee: {
            sap: sap,
            name: name,
            email: email,
            role: role,
            supportType: supportType,
            project: project,
            mainlanguage: mainlanguage,
            rm: rm
         }
      };

      axios
         .post(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Employee saved successfully'
            });
            document.getElementById('getEmployeesData').click();
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };

   clearForm = () => {
      this.setState({
         sap: '',
         name: '',
         email: '',
         role: '',
         supportType: '',
         project: '',
         mainlanguage: '',
         rm: '',
         message: ''
      });
   };

   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   closeAlert() {
      this.setState({ message: '' });
   }

   render() {
      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm,
         message
      } = this.state;

      return (
         <div>
            <H5>Create New Employee</H5>

            <Alert
               isOpen={message !== ''}
               portalContainer={document.getElementById(
                  'createNewEmployeeModal'
               )}
               confirmButtonText='OK'
               onConfirm={() => this.closeAlert()}
               icon='issue'
               intent='none'
            >
               <H5>{message}</H5>
            </Alert>

            <Form className='mb-2' ref='createProjectForm'>
               <HTMLTable condensed>
                  <tbody>
                     <tr>
                        <td>
                           <FormGroup
                              label='SAP'
                              labelFor='sap'
                              labelInfo='(required)'
                           ></FormGroup>
                        </td>
                        <td>
                           <InputGroup
                              id='sap'
                              onChange={this.onChange}
                              placeholder='SAP Number...'
                              type='number'
                              value={sap}
                              fill
                           />
                        </td>
                     </tr>
                     <tr>
                        <td colSpan='2'>
                           <FormGroup
                              label='Email'
                              labelFor='email'
                              labelInfo='(required)'
                           />
                        </td>
                        <td>
                           <InputGroup
                              id='email'
                              onChange={this.onChange}
                              placeholder='Email...'
                              type='email'
                              value={email}
                              fill
                           />
                        </td>
                     </tr>
                     <tr>
                        <td colSpan='2'>
                           <FormGroup
                              label='Name'
                              labelFor='name'
                              labelInfo='(required)'
                           />
                        </td>
                        <td>
                           <InputGroup
                              id='name'
                              onChange={this.onChange}
                              placeholder='Name...'
                              type='text'
                              value={name}
                              fill
                           />
                        </td>
                     </tr>
                     <tr>
                        <td>
                           <FormGroup
                              label='Role'
                              labelFor='role'
                              labelInfo='(required)'
                           ></FormGroup>
                        </td>
                        <td>
                           <EmployeeRoleOptions
                              id='role'
                              onChange={this.onChange}
                              placeholder='Role...'
                              value={role}
                              fill
                           />
                        </td>
                     </tr>
                     <tr>
                        <td>
                           <FormGroup
                              label='Support Type'
                              labelFor='supportType'
                              labelInfo='(required)'
                           ></FormGroup>
                        </td>
                        <td>
                           <SupportTypeOptions
                              id='supportType'
                              onChange={this.onChange}
                              placeholder='Support Type...'
                              value={supportType}
                              fill
                           />
                        </td>
                        <td>
                           <FormGroup
                              label='Project'
                              labelFor='project'
                              labelInfo='(required)'
                           />
                        </td>
                        <td>
                           <ProjectOptions
                              id='project'
                              onChange={this.onChange}
                              placeholder='Project...'
                              value={project}
                              fill
                           />
                        </td>
                     </tr>
                     <tr>
                        <td>
                           <FormGroup
                              label='Main Language'
                              labelFor='mainLanguage'
                              labelInfo='(required)'
                           />
                        </td>
                        <td>
                           <LanguageOptions
                              id='mainlanguage'
                              onChange={this.onChange}
                              placeholder='Language..'
                              value={mainlanguage}
                              fill
                           />
                        </td>
                        <td>
                           <FormGroup
                              label='RM'
                              labelFor='rm'
                              labelInfo='(required)'
                           />
                        </td>
                        <td>
                           <RMOptions
                              id='rm'
                              onChange={this.onChange}
                              placeholder='RM...'
                              value={rm}
                              fill
                           />
                        </td>
                     </tr>
                  </tbody>
               </HTMLTable>

               <div className='text-right'>
                  <ButtonGroup>
                     <Button icon='refresh' onClick={this.clearForm}>
                        Clear
                     </Button>
                     <Button
                        icon='saved'
                        intent='primary'
                        onClick={this.onSubmit}
                     >
                        Add
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
