import React, { Component } from 'react';
import {
   Col,
   Alert,
   ButtonGroup,
   Button,
   Form,
   FormGroup,
   FormText,
   Label,
   Input
} from 'reactstrap';
import axios from 'axios';

export default class UpdateProjectForm extends Component {
   state = {
      theme: this.props.theme,
      projectID: this.props.projectID,
      projectName: this.props.projectName,
      status: this.props.status,
      deliveryType: this.props.deliveryType,
      sm: this.props.sm,
      ops: this.props.ops,
      cim: this.props.cim,
      ras: this.props.ras,
      message: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   onSubmit = e => {
      e.preventDefault();

      const {
         id,
         projectName,
         status,
         deliveryType,
         sm,
         ops,
         cim,
         ras
      } = this.state;

      const URL = 'http://localhost:3001/api/projects/';

      const data = {
         project: {
            _id: id,
            name: projectName,
            status: status,
            deliveryType: deliveryType,
            sm: sm,
            ops: ops,
            cim: cim,
            ras: ras,
            created: ''
         }
      };

      axios
         .put(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Project updated successfully'
            });
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };

   clearForm = () => {
      this.setState({
         theme: this.props.theme,
         projectID: this.props.projectID,
         projectName: this.props.projectName,
         status: this.props.status,
         deliveryType: this.props.deliveryType,
         sm: this.props.sm,
         ops: this.props.ops,
         cim: this.props.cim,
         ras: this.props.ras
      });
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   setSelectTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   render() {
      const {
         projectName,
         status,
         deliveryType,
         sm,
         ops,
         cim,
         ras,
         message
      } = this.state;

      return (
         <div>
            {message !== '' && (
               <Alert
                  className={`bg-transparent border rounded-corners text-center ${this.alertStyle()}`}
               >
                  {message}
               </Alert>
            )}
            <Form className='mb-2' ref='createProjectForm'>
               <FormGroup row>
                  <Label
                     className='text-right'
                     for='projectName'
                     md={3}
                     size='sm'
                  >
                     Project Name
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='projectName'
                        id='projectName'
                        ref='projectName'
                        size='sm'
                        disabled
                        value={projectName}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Project Name</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='status' md={3} size='sm'>
                     Status
                  </Label>
                  <Col md={3}>
                     <Input
                        type='select'
                        name='status'
                        id='status'
                        size='sm'
                        value={status}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     >
                        <option>Retired</option>
                        <option>Delivery</option>
                     </Input>
                     <FormText>Please select Status</FormText>
                  </Col>

                  <Label
                     className='text-right'
                     for='deliveryType'
                     md={3}
                     size='sm'
                  >
                     Delivery Type
                  </Label>
                  <Col md={3}>
                     <Input
                        type='select'
                        name='deliveryType'
                        id='deliveryType'
                        size='sm'
                        value={deliveryType}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     >
                        <option>Dedicated</option>
                        <option>Shared</option>
                        <option>Hybrid</option>
                     </Input>
                     <FormText>Please select delivery type</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='sm' md={3} size='sm'>
                     Service Manager
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='sm'
                        id='sm'
                        size='sm'
                        value={sm}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     />
                     <FormText>Please provide Service Manager Name</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='ops' md={3} size='sm'>
                     Operations Manager
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='ops'
                        id='ops'
                        size='sm'
                        value={ops}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     />
                     <FormText>Please provide Operations Manager Name</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Col md={4} />
                  <Label className='text-right' for='cim' md={1} size='sm'>
                     CIM
                  </Label>
                  <Col md={3}>
                     <Input
                        type='number'
                        name='cim'
                        id='cim'
                        size='sm'
                        value={cim}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     />
                     <FormText>Please provide CIM</FormText>
                  </Col>

                  <Label className='text-right' for='ras' md={1} size='sm'>
                     RAS
                  </Label>
                  <Col md={3}>
                     <Input
                        type='number'
                        name='ras'
                        id='ras'
                        size='sm'
                        value={ras}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     />
                     <FormText>Please provide RAS</FormText>
                  </Col>
               </FormGroup>

               <div className='text-right'>
                  <ButtonGroup>
                     <Button size='sm' onClick={this.clearForm}>
                        Reset
                     </Button>
                     <Button color='primary' size='sm' onClick={this.onSubmit}>
                        Update
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
