import React, { Component } from 'react';
import { Tabs, Tab, H2, Alert, Portal, H5, Classes } from '@blueprintjs/core';
import ReportsTable from '../../components/Tables/ReportsTable';
import EmployeeDialog from '../../components/Dialogs/EmployeeDialog';
import Client from '../../Modules/Client/Client';
import TableControllers from '../../components/Tables/TableControllers';

export default class Reports extends Component {
   state = {
      activeTabID: 'Employees',
      selectedTabId: 'Employees',
      isLoading: true,
      data: [],
      openAlert: true,
      errorMessage: '',
      isEmployeesDialogOpen: false,
      kpi: '',
      reportedBy: '',
      result: '',
      completed: '',
      status: '',
      reportedAt: '',
      selected: {},
      isUserEditDialogOpen: false,
      isDeleteAlertOpen: false,
      canEdit: !false,
      canDelete: !false
   };

   getSelected = (emp, isSelected) => {
      this.setState({
         selected: emp,
         canEdit: isSelected,
         canDelete: isSelected
      });
   };

   componentDidMount() {
      this.getData();
   }

   async getData() {
      this.setLoading(true);
      var result;
      try {
         //this.resetErrorMsg();
         result = await Client.Services.ReportingService.getReports();
         this.setState({
            data: result.data
         });
      } catch (err) {
         alert(err);
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading reports');
      } finally {
         this.setLoading(false);
      }
   }

   async deleteReport(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.ReportingService.deleteReportByID(id);
         if (result.status === 200) {
            Client.Notifications.success('Report removed successfully');
         }
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete Report');
      } finally {
         this.getData();
         this.closeDeleteAlert();
      }
   }

   handleSubmit = async e => {
      e.preventDefault();
      const {
         kpi,
         reportedBy,
         result,
         completed,
         status,
         reportedAt
      } = this.state;
      var errorMsg = 'Unknown Error';
      let emp = await Client.Services.ReportingService.createReportDTO(
         kpi,
         reportedBy,
         result,
         completed,
         status,
         reportedAt
      );
      try {
         let result = await Client.Services.EmployeesService.saveEmployee(emp);
         if (result.status === 200) {
            Client.Notifications.success(`Employee ${name} successfully saved`);
            this.getData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${name} Employee`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };
   handleUpdate = async e => {
      e.preventDefault();
      const { name, email, user, type, project, team, selected } = this.state;

      var errorMsg = 'Unknown Error';
      try {
         let emp = await Client.Services.EmployeesService.getEmployeeUpdateDTO(
            selected._id,
            name,
            email,
            user,
            type,
            project,
            team
         );
         let result = await Client.Services.EmployeesService.updateEmployee(
            emp
         );
         if (result.status === 200) {
            Client.Notifications.success(`Employee ${name} successfully saved`);
            this.getData();
            console.log(result.data.employee);
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${name} Employee`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };
   openAlert(msg) {
      this.setState({ isAlertOpen: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ isAlertOpen: false, errorMessage: '' });
   }

   resetErrorMsg() {
      this.setState({ errorMessage: '' });
   }

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openEmployeesDialog() {
      this.setState({
         isEmployeesDialogOpen: true
      });
   }
   closeEmployeesDialog() {
      this.setState({
         isEmployeesDialogOpen: false
      });
   }

   openUserEditDialog = async e => {
      e.preventDefault();
      const { selected } = this.state;
      var errorMsg = 'Unknown Error';
      try {
         let result = await Client.Services.EmployeesService.getEmployeeById(
            selected._id
         );
         if (result.status === 200) {
            let emp = result.data;
            this.setState({
               isUserEditDialogOpen: true,
               name: emp.name,
               email: emp.email,
               user: emp.user._id,
               type: emp.type._id,
               project: emp.project._id,
               team: emp.team._id
            });
         }
      } catch (err) {
         console.log(err);
         this.setState({
            isUserEditDialogOpen: true,
            name: selected.name,
            email: selected.email,
            user: selected.user._id,
            type: selected.type._id,
            project: selected.project._id,
            team: selected.team._id
         });
      }
   };
   closeUserEditDialog() {
      this.setState({
         isUserEditDialogOpen: false
      });
   }

   openDeleteAlert() {
      this.setState({
         isDeleteAlertOpen: true
      });
   }
   closeDeleteAlert() {
      this.setState({
         isDeleteAlertOpen: false
      });
   }

   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }

   handleTabChange = activeTabID => {
      this.setState({
         activeTabID: activeTabID,
         selectedTabId: activeTabID
      });
   };
   clearForm() {
      this.setState({
         name: '',
         email: '',
         user: '',
         type: '',
         project: '',
         team: '',
         isAlertOpen: false,
         errorMessage: ''
      });
   }

   renderControlls() {
      if (this.state.selectedTabId === 'Employees') {
         return (
            <TableControllers
               onEdit={this.openUserEditDialog}
               canEdit={this.state.canEdit}
               onAdd={() => this.openEmployeesDialog()}
               onDelete={() => this.openDeleteAlert()}
               canDelete={this.state.canDelete}
               onRefresh={() => this.getData()}
            />
         );
      }
   }

   render() {
      const {
         data,
         isLoading,
         email,
         name,
         team,
         project,
         user,
         type,
         isAlertOpen,
         selected
      } = this.state;

      return (
         <div id='AdminPanel'>
            <div className='mt-2'>
               <H2>Employees Panel</H2>
            </div>
            <Tabs
               onChange={this.handleTabChange}
               selectedTabId={this.state.selectedTabId}
               large
            >
               <Tab
                  id='Employees'
                  title='Employees'
                  panel={
                     <ReportsTable
                        data={data}
                        onRefresh={() => this.getData()}
                        isLoading={isLoading}
                        onSelection={this.getSelected}
                     />
                  }
               />
               <Tabs.Expander />
               {this.renderControlls()}
            </Tabs>
            <EmployeeDialog
               isOpen={this.state.isUserEditDialogOpen}
               toOpen={() => this.openUserEditDialog()}
               toClose={() => this.closeUserEditDialog()}
               name={name}
               email={email}
               user={user}
               type={type}
               project={project}
               team={team}
               onChange={this.onChange}
               handleSubmit={this.handleUpdate}
               buttonCaption='Update'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            />
            <EmployeeDialog
               isOpen={this.state.isEmployeesDialogOpen}
               toOpen={() => this.openEmployeesDialog()}
               toClose={() => this.closeEmployeesDialog()}
               name={name}
               email={email}
               user={user}
               type={type}
               project={project}
               team={team}
               onChange={this.onChange}
               handleSubmit={this.handleSubmit}
               buttonCaption='Add'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            />
            <Alert
               isOpen={this.state.isDeleteAlertOpen}
               confirmButtonText='Delete'
               onConfirm={() => this.deleteUser(selected._id)}
               cancelButtonText='Cancel'
               onCancel={() => this.closeDeleteAlert()}
               intent='danger'
               icon='issue'
               ussePortal={
                  <Portal container={document.getElementById('AdminPanel')} />
               }
            >
               <H5>Are you sure that you want to delete this user?</H5>
               <strong>{`User: ${selected.username}`}</strong>
               <p></p>
               <strong
                  className={Classes.TEXT_MUTED}
               >{`Will be permanentyly deleted...`}</strong>
            </Alert>
         </div>
      );
   }
}
