/**
 * @class User
 * @description User DTO class
 * @property {String} username
 * @property {String} email
 * @property {Enum} role ['USER','TL','SM','OPS',ADMIN','ROOT']
 * @property {ObjectId} usertype -> ['USER','TL','SM','OPS',ADMIN','ROOT']
 * @property {ObjectID} employeeID
 */
export default class User {
   /**
    * @public
    * @constructor
    * @param {String} username
    * @param {String} email
    * @param {String} password
    * @param {Enum} role ['USER','TL','SM','OPS',ADMIN','ROOT']
    * @param {ObjectId} usertype -> ['USER','TL','SM','OPS',ADMIN','ROOT']
    * @param {ObjectID} employeeID
    */
   constructor(username, email, password, role, usertype, employeeID) {
      //STRING
      this.username = username;
      //STRING
      this.email = email;
      //enum ['USER','ADMIN']

      //string
      this.password = password;

      this.role = role;
      //id
      this.usertype = usertype;
      //id
      this.employeeInfo = employeeID;
   }
   /**
    * @public
    * @returns {User} DTO
    */
   getUserDTO() {
      return {
         user: {
            username: this.username,
            email: this.email,
            password: this.password,
            role: this.role,
            usertype: this.usertype,
            employeeInfo: this.employeeInfo
         }
      };
   }
}
