/*Imports*/
import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import Auth from './Modules/Auth/Auth';

/*CSS import*/
import 'bootstrap/dist/css/bootstrap.css';
import '@fortawesome/fontawesome-free/css/all.css';
import './App.css';

/*Component import*/
import MainDashboard from './components/MainDashboard/MainDashboard';
import Settings from './components/Settings/Settings';
import Login from './views/Login/Login';
import Home from './views/Home/Home';
import MainProjects from './components/MainProjects/MainProjects';
import MainReports from './components/MainReports/MainReports';
import MainAdminPanel from './components/MainAdminPanel/MainAdminPanel';

/**
 *    Testing BlueprintUI
 */
import '@blueprintjs/core/lib/css/blueprint.css';
import BluePrintView from './views/BluePrintView/BluePrintView';

/**
 *    Global UI Manager for theme switching
 */
import UIManager from './Modules/UIManager/UIManager';

/*CSS import - theming : BOOTSTRAP*/
import './custom.css';
import './blueprint_custom.css';

export default class App extends Component {
   render() {
      document.body.className =
         UIManager.getTheme() === 'dark' ? 'bp3-dark' : 'bp3-body';

      return (
         <Router>
            <div className='App'>
               <Route exact path='/' component={Login} />
               <Route path='/login/' component={Login} />
               <PrivateRoute path='/Home' component={Home} />
               <PrivateRoute path='/MainDashboard/' component={MainDashboard} />
               <PrivateRoute path='/Project/' component={MainProjects} />
               <PrivateRoute path='/Report/' component={MainReports} />
               <PrivateRoute path='/Settings/' component={Settings} />
               <PrivateRoute path='/AdminPanel/' component={MainAdminPanel} />
               <PrivateRoute path='/BlueView/' component={BluePrintView} />
            </div>
         </Router>
      );
   }
}

const PrivateRoute = ({ component: Component, ...rest }) => {
   return (
      // Show the component only when the user is logged in
      // Otherwise, redirect the user to /signin page
      <Route
         {...rest}
         render={props =>
            Auth.isUserAuthenticated() ? (
               <Component {...props} />
            ) : (
               <Redirect to='/login' />
            )
         }
      />
   );
};
