import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   ContextMenu,
   Dialog,
   Icon,
   Card
} from '@blueprintjs/core';
import Client from '../../../../Modules/Client/Client';
import EmployeeRoleForm from '../../../../components/Forms/EmployeeForms/EmployeeRoleForm';
import CardHeader from '../../../../components/Card/CardHeader';
import CardBody from '../../../../components/Card/CardBody';
import TableContextMenu from './TableContextMenu';

export default class RolesTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      roleForEdit: {}
   };
   isEligable() {
      let role = Client.User.getRole();
      if (role === 'ROOT') return false;
      if (role === 'ADMIN') return false;
      return true;
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   async deleteRole(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.EmployeeRolesService.deleteEmployeeRoleByID(
            id
         );
         if (result.status === 200) {
            Client.Notifications.success('Role removed successfully');
         }
      } catch (err) {
         console.log(err);
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete role');
      } finally {
         this.props.onRefresh();
      }
   }
   editRole(role) {
      this.setState({
         showEditCard: true,
         roleForEdit: role
      });
   }
   closeEditUser() {
      this.setState({ showEditCard: false });
   }
   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th>#</th>
               <th>Role</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { roles } = this.props;
      return roles.map((role, key) => {
         return (
            <tr key={role._id} onContextMenu={e => this.handeClick(e, role)}>
               <td>{key}</td>
               <td>{role.role}</td>
            </tr>
         );
      });
   }

   getValue(val) {
      if (val === undefined || val === null) {
         return 'N/A';
      }
      return val;
   }
   getObjectValue(obj, val) {
      if (obj === undefined || obj === null) {
         return 'N/A';
      }
      if (obj[val] === undefined || obj[val] === null) {
         return 'N/A';
      }
      return obj[val];
   }

   handeClick(e, role) {
      e.preventDefault();
      ContextMenu.show(this.renderContextMenu(e, role), {
         left: e.clientX,
         top: e.clientY
      });
   }
   renderContextMenu(e, role) {
      return (
         <TableContextMenu
            role={role}
            isEligable={this.isEligable()}
            onEdit={() => this.editRole(role)}
            onRemove={() => this.deleteRole(role._id)}
         />
      );
   }
   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load roles table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => this.getData()}
               ></Button>
            </NonIdealState>
         );
      }

      if (this.props.roles.length === 0) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>There are no Roles in database </strong>
               </H5>
               <i>No data to be displayed</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={this.props.onRefresh}
               ></Button>
            </NonIdealState>
         );
      }

      return (
         <div className='overflow-x-scroll'>
            <HTMLTable condensed striped interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }
   render() {
      const { roleForEdit } = this.state;
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }

      return (
         <div>
            {this.renderContent()}
            <Dialog
               isOpen={this.state.showEditCard}
               onClose={() => this.closeEditUser()}
               canEscapeKeyClose={true}
            >
               <Card>
                  <CardHeader>
                     <Icon icon='form' className='header-icon' />
                     Edit User
                     <div className='float-right'>
                        <Icon
                           icon='cross'
                           onClick={() => this.closeEditUser()}
                        />
                     </div>
                  </CardHeader>
                  <CardBody className='card-body'>
                     <EmployeeRoleForm
                        role={roleForEdit.role}
                        buttonCaption='Update'
                     />
                  </CardBody>
               </Card>
            </Dialog>
         </div>
      );
   }
}
RolesTable.defaultProps = {
   roles: [],
   onRefresh: function() {
      return;
   }
};
