import React, { Component } from 'react';
import {
   Row,
   Col,
   Card,
   CardBody,
   Button,
   Form,
   FormGroup,
   Input,
   InputGroup,
   InputGroupAddon,
   InputGroupText,
   Alert
} from 'reactstrap';
import Auth from '../../Modules/Auth/Auth';
import MainContainer from '../../components/MainContainer/MainContainer';
import UIManager from '../../Modules/UIManager/UIManager';
import axios from 'axios';

import './Login.css';

export default class Login extends Component {
   state = {
      theme: UIManager.getTheme(),
      buttonColor: 'secondary',
      alertVisible: true,
      isLoading: false,
      authenticated: false,
      message: '',
      email: '',
      password: ''
   };

   onSubmit = e => {
      e.preventDefault();

      const { email, password } = this.state;

      const data = {
         user: {
            email: email,
            password: password
         }
      };

      const url = '/api/users/login';

      axios
         .post(url, data)
         .then(result => {
            //localStorage.setItem('jwtToken', result.data.token);
            Auth.authenticateUser(result.data.user);
            if (Auth.isUserAuthenticated()) {
               this.setState({ message: 'ok' });
               this.props.history.push('/Home');
            } else {
               this.setState({
                  message: 'Login failed'
               });
            }
         })
         .catch(error => {
            this.setState({
               message: 'Login failed. Username or password not match'
            });
            console.log(error);
         });
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
      this.setLoginButtonColor();
      this.resetMessage();
   };

   resetMessage() {
      this.setState({ message: '' });
   }

   setLoginButtonColor() {
      const { email, password } = this.state;
      if (email && password) {
         this.setState({
            buttonColor: 'primary'
         });
      }
   }

   render() {
      const { email, password, message, theme } = this.state;

      return (
         <MainContainer theme={theme} displayBgImage={true}>
            <Row>
               <Col
                  xl={{ size: 4, offset: 4 }}
                  lg={{ size: 4, offset: 4 }}
                  md={{ size: 4, offset: 4 }}
                  sm='12'
                  xs='12'
                  className='mt-4'
               >
                  <div
                     className={`${theme} bg-login-form login-card ${
                        message ? 'shake' : ''
                     }`}
                  >
                     <Card
                        inverse
                        color={theme}
                        className='bg-transparent mt-5'
                     >
                        <CardBody className=''>
                           <Form>
                              <span className='fas fa-dice-d20 float-right' />
                              <h3 className='text-center'>Sign in</h3>
                              {message !== '' && (
                                 <Alert className='bg-transparent border border-warning text-warning rounded-corners text-center'>
                                    {message}
                                 </Alert>
                              )}
                              <hr />
                              <FormGroup>
                                 <InputGroup size='sm'>
                                    <InputGroupAddon addonType='prepend'>
                                       <InputGroupText
                                          className={
                                             theme === 'dark'
                                                ? 'bg-secondary text-white no-border'
                                                : 'bg-white text-black no-border'
                                          }
                                       >
                                          <i className='fas fa-user' />
                                       </InputGroupText>
                                    </InputGroupAddon>
                                    <Input
                                       type='email'
                                       name='email'
                                       id='email'
                                       placeholder='email'
                                       size='sm'
                                       value={email}
                                       className={
                                          theme === 'dark'
                                             ? 'bg-secondary text-white no-border'
                                             : 'bg-white text-black no-border'
                                       }
                                       onChange={this.onChange}
                                    />
                                 </InputGroup>
                              </FormGroup>
                              <FormGroup>
                                 <InputGroup size='sm'>
                                    <InputGroupAddon addonType='prepend'>
                                       <InputGroupText
                                          className={
                                             theme === 'dark'
                                                ? 'bg-secondary text-white no-border'
                                                : 'bg-white text-black no-border'
                                          }
                                       >
                                          <i className='fas fa-key' />
                                       </InputGroupText>
                                    </InputGroupAddon>
                                    <Input
                                       color={this.state.buttonColor}
                                       type='password'
                                       name='password'
                                       id='password'
                                       placeholder='password'
                                       size='sm'
                                       value={password}
                                       className={
                                          theme === 'dark'
                                             ? 'bg-secondary text-white no-border'
                                             : 'bg-white text-black no-border'
                                       }
                                       onChange={this.onChange}
                                    />
                                 </InputGroup>
                              </FormGroup>
                              <Button
                                 type='submit'
                                 className='float-right'
                                 size='sm'
                                 color={this.state.buttonColor}
                                 onClick={this.onSubmit}
                              >
                                 Login
                              </Button>
                           </Form>
                        </CardBody>
                     </Card>
                  </div>
               </Col>
            </Row>
         </MainContainer>
      );
   }
}
