import React, { Component } from "react";
import "@fortawesome/fontawesome-free/css/all.min.css";
import "./TopBar.css";
import { BrowserRouter as Router, Link } from "react-router-dom";
import {
   UncontrolledDropdown,
   DropdownToggle,
   DropdownMenu,
   DropdownItem
} from "reactstrap";

export default class TopBar extends Component {
   setTheme(theme) {
      if (theme === "dark") {
         return "navbar-dark bg-dark";
      }
      if (theme === "light") {
         return "navbar-light bg-light";
      } else {
         return "navbar-light bg-light";
      }
   }

   render() {
      return (
         <Router>
            <nav
               className={`navbar fixed-top navbar-expand ${this.setTheme(
                  this.props.theme
               )}`}
            >
               <a className="navbar-brand" href="/Home">
                  <span class="fas fa-dice-d20" />
               </a>
               <ul className="navbar-nav mr-auto">
                  <li className="nav-icon">
                     <UncontrolledDropdown nav>
                        <DropdownToggle nav className="nav-icon">
                           <span className="fas fa-th" />
                        </DropdownToggle>
                        <DropdownMenu left>
                           <DropdownItem>Get Help</DropdownItem>
                        </DropdownMenu>
                     </UncontrolledDropdown>
                  </li>
                  {this.props.children}
               </ul>
               <ul className="navbar-nav text-white">
                  <li className="nav-icon">
                     <UncontrolledDropdown nav inNavbar>
                        <DropdownToggle nav className="nav-icon">
                           <span className="fa fa-bars" />
                        </DropdownToggle>
                        <DropdownMenu right>
                           <DropdownItem>
                              <Link to="/Settings/">
                                 <span class="fas fa-cogs mr-2"> </span>
                                 Settings
                              </Link>
                           </DropdownItem>
                           <DropdownItem>
                              <span class="fas fa-user-cog mr-2"> </span>
                              User Profile
                           </DropdownItem>
                           <DropdownItem divider />
                           <DropdownItem>
                              <span class="fas fa-power-off mr-2"> </span>
                              Logout
                           </DropdownItem>
                        </DropdownMenu>
                     </UncontrolledDropdown>
                  </li>
               </ul>
            </nav>
         </Router>
      );
   }
}
