import React, { Component } from 'react';
import { TabContent, TabPane, Row } from 'reactstrap';
import UIManager from '../../Modules/UIManager/UIManager';

import TopBar from '../Nav/TopBar/TopBar';
import LeftBar from '../Nav/LeftBar/LeftBar';
import NavItem from '../Nav/NavItem/NavItem';
import MainContainer from '../MainContainer/MainContainer';
import ContactTargetCard from '../Card/ContactTargetCard/ContactTargetCard';
import WorkingHoursCard from '../Card/WorkingHoursCard/WorkingHoursCard';
import CreateUser from '../Users/CreateUser';
import ProjectsManagerCard from '../Card/ProjectCard/ProjectsManagerCard';
import EmployeeCard from '../Card/EmployeeCard/EmployeeCard';
import SLACard from '../Card/SLACard/SLACard';

export default class MainAdminPanel extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         showReportFilter: false,
         activeTab: '1'
      };
   }

   setBgByTheme(theme) {
      if (theme === 'dark') {
         return 'main-data-bg-dark';
      }
      if (theme === 'light') {
         return 'main-data-bg-light';
      } else {
         return 'main-data-bg-dark';
      }
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   render() {
      const { theme } = this.state;

      return (
         <MainContainer theme={theme}>
            <TopBar theme={theme} />
            <LeftBar theme={theme}>
               <NavItem
                  icon='fa fa-users-cog'
                  id='tab1'
                  active={this.state.activeTab === '1'}
                  onClick={() => {
                     this.switchTab('1');
                  }}
               />
               <NavItem
                  icon='fa fa-project-diagram'
                  id='tab2'
                  active={this.state.activeTab === '2'}
                  onClick={() => {
                     this.switchTab('2');
                  }}
               />
               <NavItem
                  icon='fas fa-tasks'
                  id='tab3'
                  active={this.state.activeTab === '3'}
                  onClick={() => {
                     this.switchTab('3');
                  }}
               />
               <NavItem
                  icon='fas fa-users'
                  id='tab4'
                  active={this.state.activeTab === '4'}
                  onClick={() => {
                     this.switchTab('4');
                  }}
               />
               <NavItem
                  icon='fas fa-language'
                  id='tab4'
                  active={this.state.activeTab === '5'}
                  onClick={() => {
                     this.switchTab('5');
                  }}
               />
               <NavItem
                  icon='fa fa-bullseye'
                  id='tab4'
                  active={this.state.activeTab === '6'}
                  onClick={() => {
                     this.switchTab('6');
                  }}
               />
            </LeftBar>
            <TabContent activeTab={this.state.activeTab} className='mb-5'>
               <TabPane tabId='1'>
                  Users
                  <CreateUser theme={this.state.theme} />
               </TabPane>
               <TabPane tabId='2'>
                  Projects
                  <ProjectsManagerCard theme={theme} />
               </TabPane>
               <TabPane tabId='3'>
                  SLAs
                  <SLACard theme={theme} />
               </TabPane>
               <TabPane tabId='4'>
                  Employees
                  <EmployeeCard theme={theme} />
               </TabPane>
               <TabPane tabId='5'>Language</TabPane>
               <TabPane tabId='6'>
                  Targets
                  <Row>
                     <ContactTargetCard theme={theme} size='m' />
                     <WorkingHoursCard theme={theme} size='m' />
                  </Row>
               </TabPane>
            </TabContent>
         </MainContainer>
      );
   }
}
