class UIManager {

   static setTheme(theme) {
      localStorage.setItem("theme", theme);
   }

   static getTheme() {
      if (!this.isThemeSet()) this.setTheme("light");
      return localStorage.getItem("theme");
   }

   static isThemeSet() {
      return localStorage.getItem("theme") !== null;
   }
}

export default UIManager;
