import React, { Component } from 'react';
import StdCard from '../../../components/Card/StdCard';
import UsersTable from './UsersTable.js/UsersTable';
import Client from '../../../Modules/Client/Client';
import { NonIdealState } from '@blueprintjs/core';

export default class UsersListCard extends Component {
   state = {
      users: [],
      errorMsg: ''
   };

   componentDidMount() {
      let result;
      try {
         result = Client.Services.UsersService.getUsers();
         this.setState({
            users: result.data
         });
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error while loading users list');
      }
   }

   moreClick() {}
   renderContent() {
      const { users, errorMsg } = this.state;
      if (errorMsg) {
         return <NonIdealState></NonIdealState>;
      }
      return <UsersTable data={users} onMoreClick={() => this.moreClick()} />;
   }

   render() {
      return (
         <div>
            <StdCard headerText='Users' icon='th' size='m'>
               {this.renderContent()}
            </StdCard>
         </div>
      );
   }
}
