import React, { Component } from 'react';
import {
   Modal,
   Button,
   ButtonGroup,
   Card,
   CardBody,
   CardHeader
} from 'reactstrap';
import StdCard from '../StdCard';

import CreateWorkingHourTargetForm from '../../Forms/WorkingHoursForms/CreateWorkingHourTargetForm';
import WorkingHourTable from '../../Table/WorkingHourTable/WorkingHourTable';

export default class WorkingHoursCard extends Component {
   constructor(props) {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = {
         theme: this.props.theme,
         modal: false
      };
   }

   toggle() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   setContextInfo() {
      return 'Manage your Working Hours Targets';
   }

   setTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'table-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }

   setCardTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-dark text-white';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, modal } = this.state;

      return (
         <StdCard
            theme={theme}
            icon='fas fa-clock'
            headerText='Working Hours Targets Manager'
            size={this.props.size}
         >
            <div>
               <h5>
                  {this.setContextInfo()}
                  <ButtonGroup className='float-right'>
                     <Button size='sm' color='primary' onClick={this.toggle}>
                        <span className='fas fa-plus' />
                     </Button>
                  </ButtonGroup>
               </h5>
               <hr />
               <WorkingHourTable theme={theme} />
            </div>
            <Modal isOpen={modal} toggle={this.toggle} className=''>
               <Card className={this.setCardTheme()}>
                  <CardHeader>
                     <span class='fas fa-chart-line header-icon' />
                     New Working Hour Target
                     <span
                        className='far fa-window-restore float-right header-icon text-secondary'
                        onClick={this.toggle}
                     />
                  </CardHeader>

                  <CardBody className='card-body'>
                     <CreateWorkingHourTargetForm theme={theme} />
                  </CardBody>
               </Card>
            </Modal>
         </StdCard>
      );
   }
}
