import React, { Component } from 'react';
import { Row, Form, FormGroup, Label, CustomInput } from 'reactstrap';

import StdCard from '../Card/StdCard';

import UIManager from '../../Modules/UIManager/UIManager';

export default class Settings extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         showReportFilter: false,
         activeTab: '1'
      };
   }

   render() {
      return (
         <Row>
            <StdCard
               icon='fas fa-cogs'
               headerText='Application settings'
               theme={this.state.theme}
            >
               <h6 for='exampleCheckbox mb-o'>
                  <span className='fas fa-palette mr-2' />
                  User Interface
               </h6>
               <hr />
               <Form>
                  <FormGroup>
                     <Label for='exampleCheckbox mb-o'>Theme</Label>
                     <CustomInput
                        type='switch'
                        id='UITheme'
                        name='UITheme'
                        label='Enable dark mode'
                        onChange={this.switchTheme}
                        checked={this.isDarkModeEnabled()}
                     />
                  </FormGroup>
               </Form>
            </StdCard>
         </Row>
      );
   }
}
