import React, { Component } from "react";
import axios from "axios";
import StdCard from "../Card/StdCard";
import Select from "../Buttons/Select/Select";
import {
   Row,
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   Label,
   Input
} from "reactstrap";

export default class CreateUser extends Component {
   state = {
      theme: "dark",
      userType: "",
      userTypes: [
         {
            name: "Standard",
            value: "Standard"
         },
         {
            name: "Admin",
            value: "Admin"
         },
         {
            name: "Root",
            value: "Root"
         }
      ],
      username: "",
      email: "",
      password: "",
      project: "",
      projects: [
         {
            name: "GDC",
            value: "GDC"
         }
      ],
      created: false,
      message: "",
      isLoading: ""
   };

   onSubmit = e => {
      e.preventDefault();
      const { username, password, userType, email, project } = this.state;

      const URL = "http://localhost:3001/api/users/create/";

      const userData = {
         email: email,
         password: password
      };

      alert(JSON.stringify(userData));

      axios
         .post(URL, userData)
         .then(res => {
            this.setState({
               created: res.data.created
            });
         })
         .catch(err => {
            this.setState({
               created: "error",
               message: this.data.message
            });
            console.log(err);
         });
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   setInputTheme(theme) {
      if (theme === "dark") {
         return "table-input-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   render() {
      const { message, email, password, username } = this.state;

      return (
         <StdCard
            headerText="Create new user"
            icon="fas fa-user"
            theme={this.state.theme}
         >
            <Form>
               <Row>
                  <Col md={12}>
                     {message !== "" && (
                        <Alert className="bg-transparent border border-warning text-warning rounded-corners text-center">
                           {message}
                        </Alert>
                     )}
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="project">User Type</Label>
                        <Select
                           options={this.state.userTypes}
                           theme={this.state.theme}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="project">Project</Label>
                        <Select
                           options={this.state.projects}
                           theme={this.state.theme}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="email">Username</Label>
                        <Input
                           type="text"
                           name="username"
                           id="username"
                           size="sm"
                           value={username}
                           onChange={this.onChange}
                           placeholder="Please provide username"
                           className={this.setInputTheme(this.state.theme)}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={6}>
                     <FormGroup>
                        <Label for="email">Email</Label>
                        <Input
                           type="email"
                           name="email"
                           id="exampleEmail"
                           size="sm"
                           placeholder="Please provide email"
                           value={email}
                           onChange={this.onChange}
                           className={this.setInputTheme(this.state.theme)}
                        />
                     </FormGroup>
                  </Col>
                  <Col md={12}>
                     <FormGroup>
                        <Label for="password">Password</Label>
                        <Input
                           type="password"
                           name="password"
                           id="password"
                           size="sm"
                           placeholder="Please provide temporary password"
                           value={password}
                           onChange={this.onChange}
                           className={this.setInputTheme(this.state.theme)}
                        />
                     </FormGroup>
                  </Col>
               </Row>
               <div className="text-right">
                  <Button color="primary" onClick={this.onSubmit}>
                     Create
                  </Button>
               </div>
            </Form>
         </StdCard>
      );
   }
}
