import React, { Component } from 'react';
import {
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   Label,
   Input,
   ButtonGroup
} from 'reactstrap';
import axios from 'axios';

export default class CreateWorkingHourTargetForm extends Component {
   state = {
      theme: this.props.theme,
      year: new Date().getFullYear(),
      month: '',
      target: 160,
      message: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   onSubmit = e => {
      e.preventDefault();

      const { year, month, target } = this.state;

      const URL = '/api/workingHours/';

      const data = {
         target: {
            year: year,
            month: month,
            target: target
         }
      };

      axios
         .post(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Working Hour Target saved successfully'
            });
            document.getElementById('getWorkingHoursTargetsData').click();
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };

   clearForm = () => {
      this.setState({
         year: new Date().getFullYear,
         month: new Date().getMonth,
         target: 160,
         message: ''
      });
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   render() {
      const { year, month, target, message } = this.state;

      return (
         <div>
            <h5>Create New Working Hour Target</h5>
            <hr />
            {message !== '' && (
               <Alert
                  className={`bg-transparent border rounded-corners text-center ${this.alertStyle()}`}
               >
                  {message}
               </Alert>
            )}

            <Form className='mb-3'>
               <FormGroup row>
                  <Label className='text-right' for='email' md={3} size='sm'>
                     Year
                  </Label>
                  <Col md={4}>
                     <Input
                        type='number'
                        id='year'
                        name='year'
                        value={year}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='Project' md={3} size='sm'>
                     Month
                  </Label>
                  <Col md={4}>
                     <Input
                        type='select'
                        id='month'
                        name='month'
                        value={month}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     >
                        <option selected>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                        <option>6</option>
                        <option>7</option>
                        <option>8</option>
                        <option>9</option>
                        <option>10</option>
                        <option>11</option>
                        <option>12</option>
                     </Input>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='Project' md={3} size='sm'>
                     Target
                  </Label>
                  <Col md={4}>
                     <Input
                        type='number'
                        id='target'
                        name='target'
                        value={target}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>

               <div className='text-right'>
                  <ButtonGroup>
                     <Button size='sm' onClick={this.clearForm}>
                        Clear
                     </Button>
                     <Button color='primary' size='sm' onClick={this.onSubmit}>
                        Add
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
