import API from '../../API';
import Request from '../../Requests/Request';
import EmployeeRoleDTO from './DTO/EmployeeRole';

export default class EmployeesRolesService {
   static createEmployeeRoleDTO(role) {
      return new EmployeeRoleDTO(role).getEmployeeDTO();
   }
   static async createEmployeeRole(role) {
      return Request.post(
         API.ROUTES.EMPLOYEES_ROLES.CREATE_EMPLOYEES_ROLE,
         role
      )
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
   static async getEmployeeRoles() {
      return Request.get(API.ROUTES.EMPLOYEES_ROLES.GET_EMPLOYEES_ROLES)
         .then(res => {
            console.log(res.data);
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
   static async getEmployeeRoleByID(id) {
      return Request.get(
         API.ROUTES.EMPLOYEES_ROLES.GET_EMPLOYEES_ROLE_BY_ID + id
      )
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
   static async deleteEmployeeRoleByID(id) {
      return Request.delete(
         API.ROUTES.EMPLOYEES_ROLES.DELETE_EMPLOYEES_ROLE_BY_ID + id
      )
         .then(res => {
            return res;
         })
         .catch(err => {
            throw err;
         });
   }
}
