import React, { Component } from 'react';

export default class NavItem extends Component {
   state = {
      id: this.props.id
   };
   activate(enable) {
      if (enable) return 'active';
      return '';
   }

   render() {
      const { id, icon, active, href } = this.props;

      return (
         <li className='nav-item'>
            <a
               className={`nav-link nav-icon ${this.activate(active)}`}
               aria-haspopup='true'
               aria-expanded='false'
               id={id}
               href={href}
            >
               <span className={icon} onClick={this.props.onClick} />
            </a>
         </li>
      );
   }
}
