import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import { Button, Card, H5 } from '@blueprintjs/core';
import IconCard from './components/IconCard/IconCard';
import Client from '../../Modules/Client/Client';

export default class Home extends Component {
   state = {
      testResult: '',
      testServiceResult: []
   };
   componentDidMount() {
      //this.getUserData();
   }

   async getUserData() {
      let result = await Client.testingCurrentUserAuth();

      this.setState({
         testResult: result
      });
   }

   render() {
      return (
         <div className='container-fluid'>
            <H5>GhOSt KPI APPlication</H5>

            <Row>
               <Col xl={12} lg={12} md={12} sm={12} xs={12} className='mt-3'>
                  <Card>
                     <div className='card-body'>
                        <h3> Project info </h3>
                        <hr />
                        <h5>current version: 0.0.1</h5>
                        <hr />
                        <h5>Author: Rafal Cymbalista</h5>

                        <h5>
                           Test Authentication:{' '}
                           <Button
                              icon={this.state.testResult ? 'unlock' : 'lock'}
                              minimal
                              onClick={() => this.getUserData()}
                           ></Button>
                           <p>{this.state.testResult}</p>
                        </h5>
                     </div>
                  </Card>
               </Col>
            </Row>
         </div>
      );
   }
}
